import React, { useState, useRef, useEffect, useCallback } from 'react';
import './DAWPage.css';

// DAWã®å®šæ•°
const BEAT_WIDTH = 100; // 1æ‹ã®å¹…ï¼ˆpxï¼‰
const BEATS_PER_MEASURE = 4; // 1å°ç¯€ã‚ãŸã‚Šã®æ‹æ•°
const MEASURE_WIDTH = BEAT_WIDTH * BEATS_PER_MEASURE; // 1å°ç¯€ã®å¹…ï¼ˆ400pxï¼‰
const SUB_BEAT_WIDTH = BEAT_WIDTH / 2; // 8åˆ†éŸ³ç¬¦ã®å¹…ï¼ˆ50pxï¼‰
const TOTAL_MEASURES = 16; // è¡¨ç¤ºã™ã‚‹å°ç¯€æ•°
const TOTAL_BEATS = TOTAL_MEASURES * BEATS_PER_MEASURE; // ç·æ‹æ•°

// æ™‚é–“ãƒ¢ãƒ¼ãƒ‰ã®å®šæ•°
const TIME_MODE_TOTAL_SECONDS = 60; // è¡¨ç¤ºã™ã‚‹ç·ç§’æ•°
const PIXELS_PER_SECOND = 100; // 1ç§’ã‚ãŸã‚Šã®ãƒ”ã‚¯ã‚»ãƒ«æ•°

const DAWPage = () => {
  // ãƒ¦ãƒ‹ãƒ¼ã‚¯IDç”Ÿæˆç”¨ã®ã‚«ã‚¦ãƒ³ã‚¿ãƒ¼
  const trackIdCounterRef = useRef(1);
  // ãƒˆãƒ©ãƒƒã‚¯åã®ç•ªå·ç®¡ç†ç”¨ã‚«ã‚¦ãƒ³ã‚¿ãƒ¼
  const trackNameCounterRef = useRef(1);

  // LocalStorageã‹ã‚‰ã®è‡ªå‹•å¾©å…ƒæ©Ÿèƒ½
  const loadAutoSavedProject = () => {
    try {
      const autoSavedData = localStorage.getItem('dawProjectAutoSave');
      if (autoSavedData) {
        const projectData = JSON.parse(autoSavedData);

        // ãƒˆãƒ©ãƒƒã‚¯ã‚«ã‚¦ãƒ³ã‚¿ãƒ¼ã®å¾©å…ƒ
        if (projectData.trackNameCounter) {
          trackNameCounterRef.current = projectData.trackNameCounter;
        }
        if (projectData.trackIdCounter) {
          trackIdCounterRef.current = projectData.trackIdCounter;
        }

        // ç„¡åŠ¹ãªã‚¯ãƒªãƒƒãƒ—ã‚’ãƒ•ã‚£ãƒ«ã‚¿ãƒªãƒ³ã‚°
        const validTracks = (projectData.tracks || []).map(track => ({
          ...track,
          clips: (track.clips || []).filter(clip => {
            if (!clip.soundData || !clip.soundData.name) {
              console.warn('è‡ªå‹•ä¿å­˜ãƒ‡ãƒ¼ã‚¿ã‹ã‚‰ç„¡åŠ¹ãªã‚¯ãƒªãƒƒãƒ—ã‚’é™¤å¤–:', clip);
              return false;
            }
            return true;
          })
        }));

        return {
          tracks: validTracks.length > 0 ? validTracks : [{ 
            id: Date.now(), 
            name: 'ãƒˆãƒ©ãƒƒã‚¯ 1', 
            clips: [] 
          }],
          bpm: projectData.bpm || 120,
          isTimeMode: projectData.isTimeMode || false,
          secondsPerBeat: projectData.secondsPerBeat || 0.5
        };
      }
    } catch (error) {
      console.error('è‡ªå‹•ä¿å­˜ãƒ‡ãƒ¼ã‚¿ã®å¾©å…ƒã«å¤±æ•—:', error);
    }

    return {
      tracks: [{ 
        id: Date.now(), 
        name: 'ãƒˆãƒ©ãƒƒã‚¯ 1', 
        clips: [] 
      }],
      bpm: 120,
      isTimeMode: false,
      secondsPerBeat: 0.5
    };
  };

  const initialData = loadAutoSavedProject();
  const [tracks, setTracks] = useState(initialData.tracks);
  const [bpm, setBpm] = useState(initialData.bpm);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [audioContext, setAudioContext] = useState(null);
  const [trackHeight] = useState(80);
  const [playingAudios, setPlayingAudios] = useState(new Map());
  const [startPlayTime, setStartPlayTime] = useState(null);
  const [error, setError] = useState(null);
  const [sounds, setSounds] = useState([]);
  const [showSoundPanel, setShowSoundPanel] = useState(true);
  const [draggedClip, setDraggedClip] = useState(null);
  const [dragPreview, setDragPreview] = useState(null);
  const [draggedSoundDuration, setDraggedSoundDuration] = useState(400); // ãƒ‰ãƒ©ãƒƒã‚°ä¸­ã®éŸ³ç´ æã®é•·ã•
  const [dragOffset, setDragOffset] = useState(0); // ãƒ‰ãƒ©ãƒƒã‚°é–‹å§‹æ™‚ã®ã‚¯ãƒªãƒƒãƒ—å†…ã‚ªãƒ•ã‚»ãƒƒãƒˆ

  // æ™‚é–“ãƒ¢ãƒ¼ãƒ‰é–¢é€£ã®çŠ¶æ…‹
  const [isTimeMode, setIsTimeMode] = useState(initialData.isTimeMode); // false: æ‹å­ãƒ¢ãƒ¼ãƒ‰, true: ç§’æ•°ãƒ¢ãƒ¼ãƒ‰
  const [secondsPerBeat, setSecondsPerBeat] = useState(initialData.secondsPerBeat); // ç§’æ•°ãƒ¢ãƒ¼ãƒ‰æ™‚ã®1æ‹ã‚ãŸã‚Šã®ç§’æ•°
  const [isExporting, setIsExporting] = useState(false); // éŸ³æºå‡ºåŠ›ä¸­ãƒ•ãƒ©ã‚°
  const timelineRef = useRef(null);
  const animationFrameRef = useRef(null);
  const dragOverTimeoutRef = useRef(null);

  useEffect(() => {
    // Web Audio API ã®åˆæœŸåŒ–
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    setAudioContext(ctx);

    // LocalStorageã‹ã‚‰éŸ³ç´ æã‚’èª­ã¿è¾¼ã¿
    const savedSounds = JSON.parse(localStorage.getItem('soundRecordings') || '[]');

    // audioDataã‹ã‚‰Blobã‚’å¾©å…ƒ
    const soundsWithBlob = savedSounds.map(sound => {
      if (sound.audioData) {
        try {
          // Base64ãƒ‡ãƒ¼ã‚¿ã®æ¤œè¨¼
          if (!sound.audioData.includes(',')) {
            console.error('ç„¡åŠ¹ãªBase64ãƒ•ã‚©ãƒ¼ãƒãƒƒãƒˆ:', sound.name);
            return sound;
          }

          const base64Data = sound.audioData.split(',')[1];
          if (!base64Data || base64Data.length === 0) {
            console.error('Base64ãƒ‡ãƒ¼ã‚¿ãŒç©ºã§ã™:', sound.name);
            return sound;
          }

          const byteCharacters = atob(base64Data);
          const byteNumbers = new Array(byteCharacters.length);
          for (let i = 0; i < byteCharacters.length; i++) {
            byteNumbers[i] = byteCharacters.charCodeAt(i);
          }
          const byteArray = new Uint8Array(byteNumbers);

          // Blobã‚µã‚¤ã‚ºã®æ¤œè¨¼
          if (byteArray.length === 0) {
            console.error('Blobãƒ‡ãƒ¼ã‚¿ãŒç©ºã§ã™:', sound.name);
            return sound;
          }

          const blob = new Blob([byteArray], { type: 'audio/wav' });

          // Blobã®æœ‰åŠ¹æ€§ã‚’ç¢ºèª
          if (blob.size === 0) {
            console.error('ä½œæˆã•ã‚ŒãŸBlobã®ã‚µã‚¤ã‚ºãŒ0ã§ã™:', sound.name);
            return sound;
          }

          return { ...sound, audioBlob: blob };
        } catch (error) {
          console.error('éŸ³å£°ãƒ‡ãƒ¼ã‚¿ã®å¾©å…ƒã«å¤±æ•—:', sound.name, error);
          return sound;
        }
      }
      return sound;
    });

    // æœ‰åŠ¹ãªéŸ³ç´ æã®ã¿ã‚’ãƒ•ã‚£ãƒ«ã‚¿ãƒªãƒ³ã‚°
    const validSounds = soundsWithBlob.filter(sound => {
      if (!sound.audioBlob) {
        console.warn('audioBlobãŒå­˜åœ¨ã—ãªã„éŸ³ç´ æã‚’ã‚¹ã‚­ãƒƒãƒ—:', sound.name);
        return false;
      }
      if (!(sound.audioBlob instanceof Blob)) {
        console.warn('ç„¡åŠ¹ãªBlobå½¢å¼ã®éŸ³ç´ æã‚’ã‚¹ã‚­ãƒƒãƒ—:', sound.name);
        return false;
      }
      if (sound.audioBlob.size === 0) {
        console.warn('ã‚µã‚¤ã‚ºãŒ0ã®BlobéŸ³ç´ æã‚’ã‚¹ã‚­ãƒƒãƒ—:', sound.name);
        return false;
      }
      return true;
    });

    setSounds(validSounds);

    // ç„¡åŠ¹ãªéŸ³ç´ æãŒã‚ã£ãŸå ´åˆã¯LocalStorageã‚’æ›´æ–°
    if (validSounds.length !== soundsWithBlob.length) {
      const validSoundsForStorage = validSounds.map(sound => ({
        ...sound,
        audioBlob: undefined // Blobã¯ä¿å­˜ã—ãªã„
      }));
      localStorage.setItem('soundRecordings', JSON.stringify(validSoundsForStorage));
    }

    return () => {
      if (ctx && ctx.state !== 'closed') {
        ctx.close().catch(error => {
          console.warn('åˆæœŸAudioContext ã®ã‚¯ãƒ­ãƒ¼ã‚ºã«å¤±æ•—:', error);
        });
      }
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
      if (dragOverTimeoutRef.current) {
        clearTimeout(dragOverTimeoutRef.current);
      }
      // å†ç”Ÿä¸­ã®éŸ³å£°ã‚’ã™ã¹ã¦åœæ­¢ãƒ»ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
      // useEffectå†…ã§playingAudiosã®æœ€æ–°å€¤ã‚’å–å¾—
      setPlayingAudios(currentPlayingAudios => {
        currentPlayingAudios.forEach(({ audio, timeoutId, audioUrl }) => {
          if (timeoutId) {
            clearTimeout(timeoutId);
          }
          if (audio) {
            audio.pause();
            audio.src = '';
          }
          if (audioUrl) {
            URL.revokeObjectURL(audioUrl);
          }
        });
        return new Map(); // ç©ºã®Mapã‚’è¿”ã™
      });
      // ã‚°ãƒ­ãƒ¼ãƒãƒ«å¤‰æ•°ã‚’ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
      if (window.currentDraggedSoundBlob) {
        window.currentDraggedSoundBlob = null;
      }
      if (window.currentDraggedSound) {
        window.currentDraggedSound = null;
      }
    };
  }, []);

  // éŸ³å£°ãƒ•ã‚¡ã‚¤ãƒ«ã®ç¶™ç¶šæ™‚é–“ã‚’å–å¾—ã—ã¦ãƒ”ã‚¯ã‚»ãƒ«å¹…ã«å¤‰æ›
  const getAudioDuration = useCallback((audioBlob, currentBpm = bpm, currentSecondsPerBeat = secondsPerBeat) => {
    return new Promise(async (resolve) => {
      if (!audioBlob || !(audioBlob instanceof Blob)) {
        resolve(400);
        return;
      }


      // AudioContextã‚’ä½¿ç”¨ã—ãŸæ–¹æ³•ã‚’å„ªå…ˆ
      if (audioContext) {
        try {
          const arrayBuffer = await audioBlob.arrayBuffer();
          const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
          const durationInSeconds = audioBuffer.duration;


          if (isFinite(durationInSeconds) && durationInSeconds > 0) {
            let pixelsPerSecond;
            if (isTimeMode) {
              // ç§’æ•°ãƒ¢ãƒ¼ãƒ‰ï¼š1æ‹ã®ç§’æ•°ã«åŸºã¥ã„ã¦ãƒ”ã‚¯ã‚»ãƒ«/ç§’ã‚’è¨ˆç®—
              const beatWidthInPixels = currentSecondsPerBeat * PIXELS_PER_SECOND;
              pixelsPerSecond = PIXELS_PER_SECOND;
            } else {
              // æ‹å­ãƒ¢ãƒ¼ãƒ‰ï¼šBPMãƒ™ãƒ¼ã‚¹
              pixelsPerSecond = (currentBpm / 60) * 100;
            }
            const widthInPixels = durationInSeconds * pixelsPerSecond;
            resolve(widthInPixels);
            return;
          }
        } catch (error) {
          console.error('AudioContextæ–¹å¼ã§ã‚¨ãƒ©ãƒ¼:', error);
        }
      }
      resolve(400);
    });
  }, [audioContext, bpm, isTimeMode, secondsPerBeat]);

  // æ™‚é–“ãƒ¢ãƒ¼ãƒ‰ã¨BPMãƒ¢ãƒ¼ãƒ‰ã‚’åˆ‡ã‚Šæ›¿ãˆã‚‹é–¢æ•°
  const toggleTimeMode = useCallback(async () => {
    const newTimeMode = !isTimeMode;
    setIsTimeMode(newTimeMode);

    // æ—¢å­˜ã®ã‚¯ãƒªãƒƒãƒ—ã®durationã‚’æ–°ã—ã„ãƒ¢ãƒ¼ãƒ‰ã§å†è¨ˆç®—
    const updatedTracks = await Promise.all(
      tracks.map(async (track) => {
        const updatedClips = await Promise.all(
          track.clips.map(async (clip) => {
            if (clip.soundData && clip.soundData.audioBlob) {
              try {
                const arrayBuffer = await clip.soundData.audioBlob.arrayBuffer();
                const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
                const durationInSeconds = audioBuffer.duration;

                let pixelsPerSecond;
                if (newTimeMode) {
                  pixelsPerSecond = PIXELS_PER_SECOND;
                } else {
                  pixelsPerSecond = (bpm / 60) * 100;
                }
                const newDuration = durationInSeconds * pixelsPerSecond;

                return { ...clip, duration: newDuration };
              } catch (error) {
                console.warn('ã‚¯ãƒªãƒƒãƒ—ã®durationå†è¨ˆç®—ã«å¤±æ•—:', error);
                return clip;
              }
            }
            return clip;
          })
        );
        return { ...track, clips: updatedClips };
      })
    );

    setTracks(updatedTracks);
  }, [isTimeMode, tracks, audioContext, bpm]);

  // ç§’æ•°ãƒ¢ãƒ¼ãƒ‰ã§ã®1æ‹ã‚ãŸã‚Šã®ç§’æ•°ã‚’å¤‰æ›´ã™ã‚‹é–¢æ•°
  const handleSecondsPerBeatChange = useCallback(async (newSecondsPerBeat) => {
    setSecondsPerBeat(newSecondsPerBeat);

    // ç§’æ•°ãƒ¢ãƒ¼ãƒ‰ã®å ´åˆã€æ—¢å­˜ã®ã‚¯ãƒªãƒƒãƒ—ã®durationã‚’å†è¨ˆç®—
    if (isTimeMode) {
      const updatedTracks = await Promise.all(
        tracks.map(async (track) => {
          const updatedClips = await Promise.all(
            track.clips.map(async (clip) => {
              if (clip.soundData && clip.soundData.audioBlob) {
                try {
                  const newDuration = await getAudioDuration(clip.soundData.audioBlob, bpm, newSecondsPerBeat);
                  return { ...clip, duration: newDuration };
                } catch (error) {
                  console.warn('ã‚¯ãƒªãƒƒãƒ—ã®durationå†è¨ˆç®—ã«å¤±æ•—:', error);
                  return clip;
                }
              }
              return clip;
            })
          );
          return { ...track, clips: updatedClips };
        })
      );

      setTracks(updatedTracks);
    }
  }, [isTimeMode, tracks, getAudioDuration, bpm]);

  // ã‚¹ãƒŠãƒƒãƒ—å‡¦ç†ï¼ˆæ‹å­ãƒ¢ãƒ¼ãƒ‰ vs ç§’æ•°ãƒ¢ãƒ¼ãƒ‰ï¼‰
  const getSnapPosition = useCallback((position) => {
    if (isTimeMode) {
      // ç§’æ•°ãƒ¢ãƒ¼ãƒ‰ï¼š1æ‹ï¼ˆç§’æ•°ï¼‰å˜ä½ã§ã‚¹ãƒŠãƒƒãƒ—
      const beatWidthInPixels = secondsPerBeat * PIXELS_PER_SECOND;
      const subBeatWidth = beatWidthInPixels / 2; // åŠæ‹ã§ã‚¹ãƒŠãƒƒãƒ—
      return Math.round(position / subBeatWidth) * subBeatWidth;
    } else {
      // æ‹å­ãƒ¢ãƒ¼ãƒ‰ï¼š8åˆ†éŸ³ç¬¦å˜ä½ã§ã‚¹ãƒŠãƒƒãƒ—
      return Math.round(position / SUB_BEAT_WIDTH) * SUB_BEAT_WIDTH;
    }
  }, [isTimeMode, secondsPerBeat]);

  // ãƒ—ãƒ¬ã‚¤ãƒ˜ãƒƒãƒ‰ã®ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³æ›´æ–°
  const updatePlayhead = useCallback(() => {
    const animate = () => {
      if (isPlaying && startPlayTime) {
        const elapsed = (Date.now() - startPlayTime) / 1000; // çµŒéæ™‚é–“ï¼ˆç§’ï¼‰

        let pixelsPerSecond;
        if (isTimeMode) {
          // ç§’æ•°ãƒ¢ãƒ¼ãƒ‰ï¼šç›´æ¥1ç§’ = PIXELS_PER_SECONDãƒ”ã‚¯ã‚»ãƒ«
          pixelsPerSecond = PIXELS_PER_SECOND;
        } else {
          // æ‹å­ãƒ¢ãƒ¼ãƒ‰ï¼šBPMã«åŸºã¥ã„ãŸãƒ”ã‚¯ã‚»ãƒ«/ç§’
          pixelsPerSecond = (bpm / 60) * 100;
        }

        const newCurrentTime = elapsed * pixelsPerSecond;

        // æœ‰åŠ¹ãªæ•°å€¤ã‹ãƒã‚§ãƒƒã‚¯
        if (isFinite(newCurrentTime) && newCurrentTime >= 0) {
          setCurrentTime(newCurrentTime);
        } else {
          console.warn('ç„¡åŠ¹ãªcurrentTime:', newCurrentTime, 'elapsed:', elapsed, 'pixelsPerSecond:', pixelsPerSecond);
        }

        // æ¬¡ã®ãƒ•ãƒ¬ãƒ¼ãƒ ã‚’è¦æ±‚
        animationFrameRef.current = requestAnimationFrame(animate);
      }
    };

    if (isPlaying && startPlayTime) {
      animate();
    }
  }, [isPlaying, startPlayTime, bpm, isTimeMode]);

  useEffect(() => {
    if (isPlaying) {
      if (!startPlayTime) {
        // å†ç”Ÿé–‹å§‹æ™‚ã«startPlayTimeã‚’è¨­å®š
        let pixelsPerSecond;
        if (isTimeMode) {
          pixelsPerSecond = PIXELS_PER_SECOND;
        } else {
          pixelsPerSecond = (bpm / 60) * 100;
        }

        if (isFinite(pixelsPerSecond) && pixelsPerSecond > 0) {
          const timeInSeconds = currentTime / pixelsPerSecond;
          if (isFinite(timeInSeconds) && timeInSeconds >= 0) {
            setStartPlayTime(Date.now() - (timeInSeconds * 1000));
          } else {
            setStartPlayTime(Date.now());
          }
        } else {
          setStartPlayTime(Date.now());
        }
      }
    } else {
      // å†ç”Ÿåœæ­¢æ™‚ã«ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ã‚’ã‚¯ãƒªã‚¢
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
        animationFrameRef.current = null;
      }
      setStartPlayTime(null);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [isPlaying, bpm, currentTime, isTimeMode]);

  // startPlayTimeãŒè¨­å®šã•ã‚ŒãŸã¨ãã«ã‚¢ãƒ‹ãƒ¡ãƒ¼ã‚·ãƒ§ãƒ³ã‚’é–‹å§‹
  useEffect(() => {
    if (isPlaying && startPlayTime) {
      updatePlayhead();
    }
  }, [isPlaying, startPlayTime, updatePlayhead]);

  // BPMå¤‰æ›´æ™‚ã®ãƒãƒ³ãƒ‰ãƒ©ãƒ¼
  const handleBpmChange = useCallback(async (newBpm) => {
    setBpm(newBpm);

    // æ—¢å­˜ã®ã‚¯ãƒªãƒƒãƒ—ã®durationã‚’æ–°ã—ã„BPMã§å†è¨ˆç®—
    const updatedTracks = await Promise.all(
      tracks.map(async (track) => {
        const updatedClips = await Promise.all(
          track.clips.map(async (clip) => {
            if (clip.soundData && clip.soundData.audioBlob) {
              try {
                const newDuration = await getAudioDuration(clip.soundData.audioBlob, newBpm);
                return { ...clip, duration: newDuration };
              } catch (error) {
                console.warn('ã‚¯ãƒªãƒƒãƒ—ã®durationæ›´æ–°ã«å¤±æ•—:', error);
                return clip;
              }
            }
            return clip;
          })
        );
        return { ...track, clips: updatedClips };
      })
    );

    setTracks(updatedTracks);
  }, [tracks, getAudioDuration]);

  // ãƒ—ãƒ­ã‚¸ã‚§ã‚¯ãƒˆä¿å­˜æ©Ÿèƒ½
  const saveProject = () => {
    try {
      const projectData = {
        version: '1.0',
        bpm: bpm,
        tracks: tracks,
        sounds: sounds.map(sound => ({
          ...sound,
          audioBlob: null, // Blobã¯åˆ¥é€”ä¿å­˜
          audioData: sound.audioData // base64ãƒ‡ãƒ¼ã‚¿ã‚’ä¿æŒ
        })),
        timestamp: Date.now(),
        trackNameCounter: trackNameCounterRef.current,
        trackIdCounter: trackIdCounterRef.current,
        isTimeMode: isTimeMode,
        secondsPerBeat: secondsPerBeat
      };

      const projectJson = JSON.stringify(projectData, null, 2);
      const blob = new Blob([projectJson], { type: 'application/json' });
      const url = URL.createObjectURL(blob);

      const link = document.createElement('a');
      link.href = url;
      link.download = `music-project-${new Date().toISOString().slice(0, 19).replace(/:/g, '-')}.json`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      URL.revokeObjectURL(url);

    } catch (error) {
      console.error('ãƒ—ãƒ­ã‚¸ã‚§ã‚¯ãƒˆä¿å­˜ã‚¨ãƒ©ãƒ¼:', error);
      setError('ãƒ—ãƒ­ã‚¸ã‚§ã‚¯ãƒˆã®ä¿å­˜ã«å¤±æ•—ã—ã¾ã—ãŸã€‚');
    }
  };

  // ãƒ—ãƒ­ã‚¸ã‚§ã‚¯ãƒˆèª­ã¿è¾¼ã¿æ©Ÿèƒ½
  const loadProject = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const projectData = JSON.parse(e.target.result);

        // ãƒãƒ¼ã‚¸ãƒ§ãƒ³ãƒã‚§ãƒƒã‚¯
        if (!projectData.version) {
          throw new Error('ä¸æ­£ãªãƒ—ãƒ­ã‚¸ã‚§ã‚¯ãƒˆãƒ•ã‚¡ã‚¤ãƒ«ã§ã™');
        }

        // éŸ³å£°ãƒ‡ãƒ¼ã‚¿å¾©å…ƒç”¨ã®ãƒ˜ãƒ«ãƒ‘ãƒ¼é–¢æ•°
        const restoreAudioBlob = (soundData) => {
          if (soundData && soundData.audioData) {
            try {
              const byteCharacters = atob(soundData.audioData.split(',')[1]);
              const byteNumbers = new Array(byteCharacters.length);
              for (let i = 0; i < byteCharacters.length; i++) {
                byteNumbers[i] = byteCharacters.charCodeAt(i);
              }
              const byteArray = new Uint8Array(byteNumbers);
