      const currentTimeInSeconds = currentTime / pixelsPerSecond;

      // å„ãƒˆãƒ©ãƒƒã‚¯ã®ã‚¯ãƒªãƒƒãƒ—ã‚’å†ç”Ÿ
      const newPlayingAudios = new Map();

