          if (!isFinite(clip.duration) || clip.duration <= 0) {
            console.warn('ç„¡åŠ¹ãªclip.duration:', clip.duration, 'ã‚¯ãƒªãƒƒãƒ—ã‚’ã‚¹ã‚­ãƒƒãƒ—ã—ã¾ã™');
            return;
