const SoundItem = ({ sound, onDragStart }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isDragging, setIsDragging] = useState(false);
  const [touchStart, setTouchStart] = useState(null);
  const [touchMove, setTouchMove] = useState(null);

  const handleDragStart = (e) => {
    // audioBlobä»¥å¤–ã®ãƒ‡ãƒ¼ã‚¿ã‚’JSONæ–‡å­—åˆ—ã¨ã—ã¦è¨­å®š
    const soundDataForTransfer = {
      ...sound,
      audioBlob: null // Blobã¯ç›´æ¥ã‚·ãƒªã‚¢ãƒ©ã‚¤ã‚ºã§ããªã„ãŸã‚ä¸€æ™‚çš„ã«nullã«
    };

    e.dataTransfer.setData('application/json', JSON.stringify(soundDataForTransfer));
    e.dataTransfer.effectAllowed = 'copy';

    // å®Ÿéš›ã®audioBlobã¯åˆ¥é€”ã‚°ãƒ­ãƒ¼ãƒãƒ«å¤‰æ•°ã§ä¿æŒ
    window.currentDraggedSoundBlob = sound.audioBlob;

    // è¦ªã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆã®onDragStarté–¢æ•°ã‚’å‘¼ã³å‡ºã—ï¼ˆéŸ³å£°ã®é•·ã•ã‚’è¨ˆç®—ï¼‰
    if (onDragStart) {
      onDragStart(sound);
    }
  };

  // ã‚¿ãƒƒãƒã‚¤ãƒ™ãƒ³ãƒˆå¯¾å¿œ
  const handleTouchStart = (e) => {
    const touch = e.touches[0];
    setTouchStart({ x: touch.clientX, y: touch.clientY });
    setIsDragging(false);

    // é•·æŠ¼ã—åˆ¤å®šç”¨ã®ã‚¿ã‚¤ãƒãƒ¼ã¯è¨­å®šã›ãšã€ç§»å‹•æ¤œçŸ¥ã§ã®ã¿ãƒ‰ãƒ©ãƒƒã‚°ã‚’é–‹å§‹
  };

  const handleTouchMove = (e) => {
    if (!touchStart) return;

    const touch = e.touches[0];
    const currentPos = { x: touch.clientX, y: touch.clientY };
    setTouchMove(currentPos);

    // ãƒ‰ãƒ©ãƒƒã‚°é–‹å§‹ã®åˆ¤å®šï¼ˆ15pxä»¥ä¸Šç§»å‹•ï¼‰- é–¾å€¤ã‚’ä¸Šã’ã¦ã‚ˆã‚Šæ„å›³çš„ãªç§»å‹•ã®ã¿ãƒ‰ãƒ©ãƒƒã‚°æ‰±ã„
    const deltaX = Math.abs(currentPos.x - touchStart.x);
    const deltaY = Math.abs(currentPos.y - touchStart.y);

    if (!isDragging && (deltaX > 15 || deltaY > 15)) {
      setIsDragging(true);
      // ã‚¹ã‚¯ãƒ­ãƒ¼ãƒ«ã‚’ä¸€æ™‚çš„ã«ç„¡åŠ¹åŒ–ï¼ˆç§»å‹•ãŒç¢ºå®šã—ã¦ã‹ã‚‰ï¼‰
      document.body.classList.add('dragging');

      // è¦ªã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆã®onDragStarté–¢æ•°ã‚’å‘¼ã³å‡ºã—
      if (onDragStart) {
        onDragStart(sound);
      }
      // ã‚°ãƒ­ãƒ¼ãƒãƒ«å¤‰æ•°ã«è¨­å®š
      window.currentDraggedSoundBlob = sound.audioBlob;
      window.currentDraggedSound = sound;
    }

    if (isDragging) {
      // passiveã‚¤ãƒ™ãƒ³ãƒˆã§ã¯preventDefaultãŒä½¿ãˆãªã„ã®ã§ã€ä»£ã‚ã‚Šã«touchActionã§ã‚¹ã‚¯ãƒ­ãƒ¼ãƒ«ã‚’åˆ¶å¾¡

      // ãƒ‰ãƒ©ãƒƒã‚°ãƒ—ãƒ¬ãƒ“ãƒ¥ãƒ¼ã®ä½ç½®ã‚’æ›´æ–°
      const dragPreview = document.querySelector('.mobile-drag-preview');
      if (dragPreview) {
        dragPreview.style.left = `${currentPos.x - 50}px`;
        dragPreview.style.top = `${currentPos.y - 20}px`;
      }

      // ãƒ‰ãƒ­ãƒƒãƒ—ã‚¿ãƒ¼ã‚²ãƒƒãƒˆã®ãƒã‚¤ãƒ©ã‚¤ãƒˆ
      const elementBelow = document.elementFromPoint(currentPos.x, currentPos.y);
      const trackElement = elementBelow?.closest('.track');

      // æ—¢å­˜ã®ãƒã‚¤ãƒ©ã‚¤ãƒˆã‚’å‰Šé™¤
      document.querySelectorAll('.track').forEach(track => {
        track.classList.remove('drag-over');
      });

      // æ–°ã—ã„ãƒã‚¤ãƒ©ã‚¤ãƒˆã‚’è¿½åŠ 
      if (trackElement) {
        trackElement.classList.add('drag-over');
      }
    }
  };

  const handleTouchEnd = (e) => {
    if (isDragging && touchMove) {
      // ãƒ‰ãƒ­ãƒƒãƒ—å‡¦ç†
      const elementBelow = document.elementFromPoint(touchMove.x, touchMove.y);
      const trackElement = elementBelow?.closest('.track');

      if (trackElement) {
        const trackId = parseInt(trackElement.dataset.trackId);
        const rect = trackElement.getBoundingClientRect();
        const timePosition = touchMove.x - rect.left;

        // ãƒ‰ãƒ­ãƒƒãƒ—ã‚¤ãƒ™ãƒ³ãƒˆã‚’ç™ºç«
        const dropEvent = new CustomEvent('mobileDrop', {
          detail: {
            trackId,
            timePosition,
            sound: sound
          }
        });
        trackElement.dispatchEvent(dropEvent);
      }
    }

    // ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
    setTouchStart(null);
    setTouchMove(null);
    setIsDragging(false);

    // SoundItem å†…ã§ã®ç›´æ¥ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
    document.body.classList.remove('dragging');

    // ãƒã‚¤ãƒ©ã‚¤ãƒˆã‚’å‰Šé™¤
    document.querySelectorAll('.track').forEach(track => {
      track.classList.remove('drag-over');
    });

    // ãƒ¢ãƒã‚¤ãƒ«ç”¨ã®ãƒ‰ãƒ©ãƒƒã‚°ãƒ—ãƒ¬ãƒ“ãƒ¥ãƒ¼ã‚’å‰Šé™¤
    const mobileDragPreview = document.querySelector('.mobile-drag-preview');
    if (mobileDragPreview) {
      mobileDragPreview.remove();
    }

    // ã‚°ãƒ­ãƒ¼ãƒãƒ«å¤‰æ•°ã‚’ã‚¯ãƒªã‚¢
    if (window.currentDraggedSoundBlob) {
      window.currentDraggedSoundBlob = null;
    }
    if (window.currentDraggedSound) {
      window.currentDraggedSound = null;
    }
  };

  const playSound = () => {
    if (sound.audioBlob && !isPlaying && !isDragging) {
      // Blobã®æœ‰åŠ¹æ€§ã‚’ãƒã‚§ãƒƒã‚¯
      if (!(sound.audioBlob instanceof Blob) || sound.audioBlob.size === 0) {
        console.error('ç„¡åŠ¹ãªaudioBlob:', {
          name: sound.name,
          isBlob: sound.audioBlob instanceof Blob,
          size: sound.audioBlob?.size
        });
        return;
      }

      const audio = new Audio();
      let audioUrl;

      try {
        audioUrl = URL.createObjectURL(sound.audioBlob);
        audio.src = audioUrl;

        audio.play()
          .then(() => {
            setIsPlaying(true);

            const handleEnded = () => {
              setIsPlaying(false);
              if (audioUrl) {
                URL.revokeObjectURL(audioUrl); // URLã‚’ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
              }
              audio.removeEventListener('ended', handleEnded);
            };

            audio.addEventListener('ended', handleEnded);

            // éŸ³å£°ã®èª­ã¿è¾¼ã¿ã‚¨ãƒ©ãƒ¼ã‚‚ãƒãƒ³ãƒ‰ãƒªãƒ³ã‚°
            audio.addEventListener('error', (error) => {
              console.error('éŸ³å£°èª­ã¿è¾¼ã¿ã‚¨ãƒ©ãƒ¼:', error);
              setIsPlaying(false);
              if (audioUrl) {
                URL.revokeObjectURL(audioUrl);
              }
            });
          })
          .catch(error => {
            console.error('éŸ³å£°å†ç”Ÿã‚¨ãƒ©ãƒ¼:', error);
            if (audioUrl) {
              URL.revokeObjectURL(audioUrl); // ã‚¨ãƒ©ãƒ¼æ™‚ã‚‚ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
            }
            setIsPlaying(false);
          });
      } catch (error) {
        console.error('createObjectURLã‚¨ãƒ©ãƒ¼:', error);
        setIsPlaying(false);
      }
    } else {
      console.error('å†ç”Ÿæ¡ä»¶ä¸æº€è¶³:', {
        hasAudioBlob: !!sound.audioBlob,
        isPlaying,
        isDragging
      });
    }
  };

  // ãƒ‰ãƒ©ãƒƒã‚°ãƒ—ãƒ¬ãƒ“ãƒ¥ãƒ¼ã‚’ä½œæˆ
  const createDragPreview = useCallback(() => {
    if (isDragging && touchMove) {
      let dragPreview = document.querySelector('.mobile-drag-preview');
      if (!dragPreview) {
        dragPreview = document.createElement('div');
        dragPreview.className = 'mobile-drag-preview';
        dragPreview.textContent = sound.name;
        dragPreview.style.cssText = `
          position: fixed;
          background: rgba(0, 123, 255, 0.8);
          color: white;
          padding: 5px 10px;
          border-radius: 4px;
          font-size: 12px;
          pointer-events: none;
          z-index: 1000;
          left: ${touchMove.x - 50}px;
          top: ${touchMove.y - 20}px;
        `;
        document.body.appendChild(dragPreview);
      }
    }
  }, [isDragging, touchMove, sound.name]);

  // ãƒ‰ãƒ©ãƒƒã‚°ãƒ—ãƒ¬ãƒ“ãƒ¥ãƒ¼ã®æ›´æ–°
  React.useEffect(() => {
    if (isDragging) {
      createDragPreview();
    }
  }, [isDragging, touchMove, createDragPreview]);

  return (
    <div
      className={`sound-item ${isDragging ? 'dragging' : ''}`}
      draggable="true"
      onDragStart={handleDragStart}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
    >
      <div className="sound-info">
        <h4>{sound.name}</h4>
        <div className="sound-tags">
