import React, { useState, useEffect } from 'react';
import './SoundLibrary.css';

const SoundLibrary = () => {
  const [sounds, setSounds] = useState([]);
  const [filteredSounds, setFilteredSounds] = useState([]);
  const [selectedTag, setSelectedTag] = useState('');
  const [searchQuery, setSearchQuery] = useState('');
  const [allTags, setAllTags] = useState([]);

  useEffect(() => {
    // LocalStorage„Åã„ÇâÈü≥Á¥†Êùê„ÇíË™≠„ÅøËæº„Åø
    const savedSounds = JSON.parse(localStorage.getItem('soundRecordings') || '[]');

    // audioData„Åã„ÇâBlob„ÇíÂæ©ÂÖÉ
    const soundsWithBlob = savedSounds.map(sound => {
      if (sound.audioData) {
        try {
          const blob = base64ToBlob(sound.audioData, 'audio/wav');
          return { ...sound, audioBlob: blob };
        } catch (error) {
          console.error('Èü≥Â£∞„Éá„Éº„Çø„ÅÆÂæ©ÂÖÉ„Å´Â§±Êïó:', error);
          return sound;
        }
      }
      return sound;
    });

    setSounds(soundsWithBlob);
    setFilteredSounds(soundsWithBlob);

    // ÂÖ®„Å¶„ÅÆ„Çø„Ç∞„ÇíÂèñÂæó
    const tags = [...new Set(soundsWithBlob.flatMap(sound => sound.tags))];
    setAllTags(tags);
  }, []);

  // Base64 „Çí Blob „Å´Â§âÊèõ„Åô„ÇãÈñ¢Êï∞
  const base64ToBlob = (base64, mimeType) => {
    const byteCharacters = atob(base64.split(',')[1]);
    const byteNumbers = new Array(byteCharacters.length);
    for (let i = 0; i < byteCharacters.length; i++) {
      byteNumbers[i] = byteCharacters.charCodeAt(i);
    }
    const byteArray = new Uint8Array(byteNumbers);
    return new Blob([byteArray], { type: mimeType });
  };

  useEffect(() => {
    // „Éï„Ç£„É´„Çø„É™„É≥„Ç∞Âá¶ÁêÜ
    let filtered = sounds;

    if (selectedTag) {
      filtered = filtered.filter(sound => sound.tags.includes(selectedTag));
    }

    if (searchQuery) {
      filtered = filtered.filter(sound => 
        sound.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        sound.tags.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase()))
      );
    }

    setFilteredSounds(filtered);
  }, [sounds, selectedTag, searchQuery]);

  const clearFilters = () => {
    setSelectedTag('');
    setSearchQuery('');
  };

  const deleteSound = (soundId) => {
    const updatedSounds = sounds.filter(sound => sound.id !== soundId);
    setSounds(updatedSounds);
    localStorage.setItem('soundRecordings', JSON.stringify(updatedSounds));

    // „Çø„Ç∞„É™„Çπ„Éà„ÇÇÊõ¥Êñ∞
    const tags = [...new Set(updatedSounds.flatMap(sound => sound.tags))];
    setAllTags(tags);
  };

  return (
    <div className="sound-library">
      <h2>üìö Èü≥„É©„Ç§„Éñ„É©„É™</h2>
      <p>ÈõÜ„ÇÅ„ÅüÈü≥Á¥†Êùê„ÇíË¶ã„Åü„Çä„ÄÅÊï¥ÁêÜ„Åó„Åü„Çä„Åß„Åç„Åæ„Åô</p>

      <div className="library-controls card">
        <div className="search-section">
          <h3>üîç Èü≥„ÇíÊé¢„Åô</h3>
          <div className="search-controls">
            <input
              type="text"
              placeholder="Èü≥„ÅÆÂêçÂâç„ÇÑ„Çø„Ç∞„ÅßÊ§úÁ¥¢..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
          </div>
        </div>

        <div className="filter-section">
          <h3>üè∑Ô∏è „Çø„Ç∞„ÅßÁµû„ÇäËæº„Åø</h3>
          <div className="tag-filters">
            <button
              className={`tag-filter-btn ${selectedTag === '' ? 'active' : ''}`}
              onClick={() => setSelectedTag('')}
            >
              „Åô„Åπ„Å¶
            </button>
            {allTags.map(tag => (
              <button
                key={tag}
                className={`tag-filter-btn ${selectedTag === tag ? 'active' : ''}`}
                onClick={() => setSelectedTag(tag)}
              >
                {tag}
              </button>
            ))}
          </div>

          {(selectedTag || searchQuery) && (
            <button onClick={clearFilters} className="clear-filters-btn">
              üóëÔ∏è „Éï„Ç£„É´„Çø„Éº„Çí„ÇØ„É™„Ç¢
            </button>
          )}
        </div>
      </div>

      <div className="library-stats">
        <div className="stats-card">
          <span className="stats-number">{sounds.length}</span>
          <span className="stats-label">Á∑èÈü≥Á¥†ÊùêÊï∞</span>
        </div>
        <div className="stats-card">
          <span className="stats-number">{filteredSounds.length}</span>
          <span className="stats-label">Ë°®Á§∫‰∏≠</span>
        </div>
        <div className="stats-card">
          <span className="stats-number">{allTags.length}</span>
          <span className="stats-label">„Çø„Ç∞Êï∞</span>
        </div>
      </div>

      <div className="sounds-grid">
        {filteredSounds.length === 0 ? (
          <div className="no-sounds">
            {sounds.length === 0 ? (
              <div>
                <p>üì≠ „Åæ„Å†Èü≥Á¥†Êùê„Åå„ÅÇ„Çä„Åæ„Åõ„Çì</p>
                <p>Èü≥„ÅÇ„Å§„ÇÅ„Éö„Éº„Ç∏„Åã„ÇâÈü≥„ÇíÈå≤Èü≥„Åó„Å¶„Åø„Åæ„Åó„Çá„ÅÜÔºÅ</p>
              </div>
            ) : (
              <div>
                <p>üîç Ê§úÁ¥¢Êù°‰ª∂„Å´Âêà„ÅÜÈü≥„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„Åß„Åó„Åü</p>
                <p>Âà•„ÅÆ„Ç≠„Éº„ÉØ„Éº„Éâ„ÇÑ„Çø„Ç∞„ÅßË©¶„Åó„Å¶„Åø„Å¶„Åè„Å†„Åï„ÅÑ</p>
              </div>
            )}
          </div>
        ) : (
          filteredSounds.map(sound => (
            <LibrarySoundCard 
              key={sound.id} 
              sound={sound} 
              onDelete={deleteSound}
            />
          ))
        )}
      </div>
    </div>
  );
};

const LibrarySoundCard = ({ sound, onDelete }) => {
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  const handleDelete = () => {
    onDelete(sound.id);
    setShowDeleteConfirm(false);
  };

  const dragStart = (e) => {
    e.dataTransfer.setData('application/json', JSON.stringify(sound));
    e.dataTransfer.effectAllowed = 'copy';
  };

  return (
    <div 
      className="library-sound-card"
      draggable
      onDragStart={dragStart}
    >
      <div className="sound-header">
        <h4>{sound.name}</h4>
        <div className="sound-actions">
          <button 
            className="delete-btn"
            onClick={() => setShowDeleteConfirm(true)}
            title="ÂâäÈô§"
          >
            üóëÔ∏è
          </button>
        </div>
      </div>

      <div className="sound-meta">
        <p className="sound-date">
          üìÖ {new Date(sound.createdAt).toLocaleDateString('ja-JP')}
        </p>
        {sound.tags.length > 0 && (
          <div className="sound-tags">
            {sound.tags.map(tag => (
              <span key={tag} className="tag small">{tag}</span>
            ))}
          </div>
        )}
      </div>

      <audio controls src={sound.url} className="sound-player" />

      <div className="drag-hint">
        üéµ DAW„Éö„Éº„Ç∏„Å´„Éâ„É©„ÉÉ„Ç∞&„Éâ„É≠„ÉÉ„Éó„Åß„Åç„Åæ„Åô
      </div>

      {showDeleteConfirm && (
        <div className="delete-confirm-overlay">
          <div className="delete-confirm-dialog">
            <h4>‚ö†Ô∏è ÂâäÈô§„ÅÆÁ¢∫Ë™ç</h4>
            <p>„Äå{sound.name}„Äç„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü</p>
            <div className="delete-confirm-actions">
              <button onClick={handleDelete} className="confirm-delete-btn">
                ÂâäÈô§„Åô„Çã
              </button>
              <button 
                onClick={() => setShowDeleteConfirm(false)}
                className="cancel-delete-btn"
              >
                „Ç≠„É£„É≥„Çª„É´
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SoundLibrary;
