import React, { useState, useRef, useEffect } from 'react';
import './SoundCollection.css';

const SoundCollection = () => {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [currentRecording, setCurrentRecording] = useState(null);
  const fileInputRef = useRef(null);

  const startRecording = async () => {
    try {
      // iOS„Åß„ÅÆ„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„ÇπÊîπÂñÑ - „Åæ„Åö„ÉÜ„Çπ„Éà„ÇíÂÆüË°å
      const hasAccess = await testMicrophoneAccess();
      if (!hasAccess) {
        return;
      }

      console.log('Èå≤Èü≥ÈñãÂßãÂá¶ÁêÜ‰∏≠...');

      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });

      console.log('Èå≤Èü≥„Çπ„Éà„É™„Éº„É†ÂèñÂæóÊàêÂäü');

      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => {
        chunks.push(e.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        const url = URL.createObjectURL(blob);
        setCurrentRecording({
          id: Date.now(),
          url: url,
          audioBlob: blob,
          name: '',
          tags: [],
          createdAt: new Date()
        });
      };

      recorder.start();
      setMediaRecorder(recorder);
      setIsRecording(true);
      console.log('Èå≤Èü≥ÈñãÂßãÂÆå‰∫Ü');

    } catch (error) {
      console.error('Èå≤Èü≥„ÅÆÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);

      let errorMessage = 'Èå≤Èü≥„ÇíÈñãÂßã„Åß„Åç„Åæ„Åõ„Çì„Åß„Åó„Åü„ÄÇ';

      if (error.name === 'NotAllowedError') {
        errorMessage = '„Éû„Ç§„ÇØ„ÅÆ‰ΩøÁî®„ÅåÊãíÂê¶„Åï„Çå„Åæ„Åó„Åü„ÄÇ„Éñ„É©„Ç¶„Ç∂„ÅÆË®≠ÂÆö„Åß„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÇíË®±ÂèØ„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ';
      } else if (error.name === 'NotFoundError') {
        errorMessage = '„Éû„Ç§„ÇØ„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„ÄÇ„Éá„Éê„Ç§„Çπ„Å´„Éû„Ç§„ÇØ„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„ÅãÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ';
      } else if (error.name === 'NotSupportedError') {
        errorMessage = '„Åä‰Ωø„ÅÑ„ÅÆ„Éñ„É©„Ç¶„Ç∂„ÅØÈå≤Èü≥Ê©üËÉΩ„Çí„Çµ„Éù„Éº„Éà„Åó„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ';
      } else if (error.name === 'NotReadableError') {
        errorMessage = '„Éû„Ç§„ÇØ„Åå‰ªñ„ÅÆ„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥„Åß‰ΩøÁî®‰∏≠„ÅÆÂèØËÉΩÊÄß„Åå„ÅÇ„Çä„Åæ„Åô„ÄÇ';
      } else if (error.message) {
        errorMessage = error.message;
      }

      alert(errorMessage);
    }
  };

  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      mediaRecorder.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
      setMediaRecorder(null);
    }
  };

  // Blob„Çí Base64 „Å´Â§âÊèõ„Åô„ÇãÈñ¢Êï∞
  const blobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });
  };

  const saveRecording = async (name, tags) => {
    if (currentRecording && name.trim()) {
      try {
        // Blob„Çíbase64„Å´Â§âÊèõ
        const base64Data = await blobToBase64(currentRecording.audioBlob);

        const savedRecording = {
          ...currentRecording,
          name: name.trim(),
          tags: tags.filter(tag => tag.trim()).map(tag => tag.trim()),
          audioData: base64Data, // base64„Éá„Éº„Çø„Çí‰øùÂ≠ò
          // audioBlob„ÅØ‰∏ÄÊôÇÁöÑ„Å™„ÇÇ„ÅÆ„Å™„ÅÆ„ÅßÂâäÈô§
          audioBlob: undefined
        };

        setRecordings([...recordings, savedRecording]);
        setCurrentRecording(null);

        // LocalStorage„Å´‰øùÂ≠òÔºàaudioBlob„ÅØÈô§Â§ñÔºâ
        const existingRecordings = JSON.parse(localStorage.getItem('soundRecordings') || '[]');
        const recordingToSave = { ...savedRecording };
        delete recordingToSave.audioBlob; // Blob„ÅØ‰øùÂ≠ò„Åó„Å™„ÅÑ
        localStorage.setItem('soundRecordings', JSON.stringify([...existingRecordings, recordingToSave]));
      } catch (error) {
        console.error('Èå≤Èü≥„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
        alert('Èå≤Èü≥„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇÂÜçÂ∫¶„ÅäË©¶„Åó„Åè„Å†„Åï„ÅÑ„ÄÇ');
      }
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('audio/')) {
      const url = URL.createObjectURL(file);
      setCurrentRecording({
        id: Date.now(),
        url: url,
        audioBlob: file,
        name: file.name.replace(/\.[^/.]+$/, ''),
        tags: [],
        createdAt: new Date()
      });
    }
  };

  // „Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÉÜ„Çπ„ÉàÊ©üËÉΩÔºàiOS„Åß„ÅÆÂïèÈ°åÂØæÁ≠ñÔºâ
  const testMicrophoneAccess = async () => {
    try {
      console.log('„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÉÜ„Çπ„ÉàÈñãÂßã');

      // HTTPSÊé•Á∂ö„ÉÅ„Çß„ÉÉ„ÇØ
      if (!checkHTTPS()) {
        alert('üîí Èå≤Èü≥Ê©üËÉΩ„Çí‰ΩøÁî®„Åô„Çã„Å´„ÅØHTTPSÊé•Á∂ö„ÅåÂøÖË¶Å„Åß„Åô„ÄÇ\n\niPhone„Åß„ÅØÁâπ„Å´„ÄÅ„Çª„Ç≠„É•„Ç¢„Å™Êé•Á∂ö„ÅåÂøÖË¶Å„Å®„Å™„Çä„Åæ„Åô„ÄÇ');
        return false;
      }

      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('„Åä‰Ωø„ÅÑ„ÅÆ„Éñ„É©„Ç¶„Ç∂„ÅØÈå≤Èü≥Ê©üËÉΩ„Çí„Çµ„Éù„Éº„Éà„Åó„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ');
      }

      // „Åæ„Åö„Éû„Ç§„ÇØ„ÅÆÊ®©ÈôêÁä∂ÊÖã„ÇíÁ¢∫Ë™ç
      if (navigator.permissions) {
        try {
          const permission = await navigator.permissions.query({ name: 'microphone' });
          console.log('„Éû„Ç§„ÇØÊ®©ÈôêÁä∂ÊÖã:', permission.state);

          if (permission.state === 'denied') {
            alert('„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÅåÊãíÂê¶„Åï„Çå„Å¶„ÅÑ„Åæ„Åô„ÄÇ„Éñ„É©„Ç¶„Ç∂„ÅÆË®≠ÂÆö„Åã„Çâ„Éû„Ç§„ÇØ„ÅÆ‰ΩøÁî®„ÇíË®±ÂèØ„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ');
            return false;
          }
        } catch (permError) {
          console.log('Ê®©ÈôêÁ¢∫Ë™ç„ÅØ„Çµ„Éù„Éº„Éà„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì:', permError);
        }
      }

      // ÂÆüÈöõ„Å´„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„Çí„ÉÜ„Çπ„Éà
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });

      // „Åô„Åê„Å´„Çπ„Éà„É™„Éº„É†„ÇíÂÅúÊ≠¢
      stream.getTracks().forEach(track => track.stop());
      console.log('„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÉÜ„Çπ„ÉàÊàêÂäü');
      return true;

    } catch (error) {
      console.error('„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÉÜ„Çπ„ÉàÂ§±Êïó:', error);

      let errorMessage = '„Éû„Ç§„ÇØ„Å´„Ç¢„ÇØ„Çª„Çπ„Åß„Åç„Åæ„Åõ„Çì„ÄÇ';

      if (error.name === 'NotAllowedError') {
        errorMessage = '„Éû„Ç§„ÇØ„ÅÆ‰ΩøÁî®„ÅåÊãíÂê¶„Åï„Çå„Åæ„Åó„Åü„ÄÇ„Éñ„É©„Ç¶„Ç∂„ÅÆË®≠ÂÆö„Åß„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÇíË®±ÂèØ„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ\n\niPhone„ÅÆÂ†¥ÂêàÔºö\n1. SafariË®≠ÂÆö > „Éó„É©„Ç§„Éê„Ç∑„Éº„Å®„Çª„Ç≠„É•„É™„ÉÜ„Ç£ > „Éû„Ç§„ÇØ\n2. „Åì„ÅÆ„Çµ„Ç§„Éà„ÇíË®±ÂèØ„Å´Ë®≠ÂÆö';
      } else if (error.name === 'NotFoundError') {
        errorMessage = '„Éû„Ç§„ÇØ„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„ÄÇ';
      } else if (error.name === 'NotSupportedError') {
        errorMessage = '„Åä‰Ωø„ÅÑ„ÅÆ„Éñ„É©„Ç¶„Ç∂„ÅØÈå≤Èü≥Ê©üËÉΩ„Çí„Çµ„Éù„Éº„Éà„Åó„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ';
      } else if (error.message) {
        errorMessage = error.message;
      }

      alert(errorMessage);
      return false;
    }
  };

  // HTTPSÊé•Á∂ö„ÉÅ„Çß„ÉÉ„ÇØÔºàiOS„Åß„ÅÆÈå≤Èü≥„Å´ÂøÖË¶ÅÔºâ
  const checkHTTPS = () => {
    if (window.location.protocol !== 'https:' && window.location.hostname !== 'localhost' && window.location.hostname !== '127.0.0.1') {
      return false;
    }
    return true;
  };

  return (
    <div className="sound-collection">
      <h2>üé§ Èü≥„ÅÇ„Å§„ÇÅ„Éö„Éº„Ç∏</h2>
      <p>Ë∫´„ÅÆÂõû„Çä„Å´„ÅÇ„ÇãÈü≥„ÇíÈå≤Èü≥„Åó„Åü„Çä„ÄÅÈü≥„Éï„Ç°„Ç§„É´„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶Èü≥Á¥†Êùê„ÇíÈõÜ„ÇÅ„Åæ„Åó„Çá„ÅÜÔºÅ</p>

      <div className="collection-actions">
        <div className="recording-section card">
          <h3>üéôÔ∏è Èü≥„ÇíÈå≤Èü≥„Åô„Çã</h3>

          {/* iOSÁî®„ÅÆË™¨Êòé */}
          <div className="ios-notice">
            <p>üì± <strong>iPhone/iPad„Çí„Åä‰Ωø„ÅÑ„ÅÆÊñπ„Å∏Ôºö</strong></p>
            <p>Èå≤Èü≥„Éú„Çø„É≥„ÇíÊäº„Åô„Å®„Éû„Ç§„ÇØ„ÅÆ‰ΩøÁî®Ë®±ÂèØ„ÇíÊ±Ç„ÇÅ„Çã„ÉÄ„Ç§„Ç¢„É≠„Ç∞„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô„ÄÇ„ÄåË®±ÂèØ„Äç„ÇíÈÅ∏Êäû„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ</p>
            <p>„ÉÄ„Ç§„Ç¢„É≠„Ç∞„ÅåË°®Á§∫„Åï„Çå„Å™„ÅÑÂ†¥Âêà„ÅØ„ÄÅSafariË®≠ÂÆö ‚Üí „Éó„É©„Ç§„Éê„Ç∑„Éº„Å®„Çª„Ç≠„É•„É™„ÉÜ„Ç£ ‚Üí „Éû„Ç§„ÇØ „Åß„Åì„ÅÆ„Çµ„Ç§„Éà„ÇíË®±ÂèØ„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ</p>
          </div>

          <div className="recording-controls">
            {!isRecording ? (
              <button 
                className="button-primary large-button record-btn"
                onClick={startRecording}
              >
                üî¥ Èå≤Èü≥ÈñãÂßã
              </button>
            ) : (
              <button 
                className="button-secondary large-button stop-btn"
                onClick={stopRecording}
              >
                ‚èπÔ∏è Èå≤Èü≥ÂÅúÊ≠¢
              </button>
            )}
          </div>
          {isRecording && (
            <div className="recording-indicator">
              <div className="pulse-dot"></div>
              Èå≤Èü≥‰∏≠...
            </div>
          )}
        </div>

        <div className="upload-section card">
          <h3>üìÅ Èü≥„Éï„Ç°„Ç§„É´„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ</h3>
          <button 
            className="button-secondary large-button"
            onClick={() => fileInputRef.current?.click()}
          >
            üìÇ „Éï„Ç°„Ç§„É´„ÇíÈÅ∏Êäû
          </button>
