const RecordingEditor = ({ recording, onSave, onCancel }) => {
  const [name, setName] = useState(recording.name);
  const [tagInput, setTagInput] = useState('');
  const [tags, setTags] = useState(recording.tags);

  const addTag = () => {
    if (tagInput.trim() && !tags.includes(tagInput.trim())) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
    }
  };

  const removeTag = (tagToRemove) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addTag();
    }
  };

  return (
    <div className="recording-editor card">
      <h3>‚úèÔ∏è Èü≥„Å´ÂêçÂâç„Çí„Å§„Åë„Çà„ÅÜ</h3>

      <audio controls src={recording.url} className="audio-preview" />

      <div className="form-group">
        <label htmlFor="soundName">Èü≥„ÅÆÂêçÂâç:</label>
        <input
          id="soundName"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="‰æã: „Éî„Ç¢„Éé„ÅÆÈü≥„ÄÅÈõ®„ÅÆÈü≥"
          className="sound-name-input"
        />
      </div>

      <div className="form-group">
        <label htmlFor="soundTags">„Çø„Ç∞:</label>
        <div className="tag-input-container">
          <input
            id="soundTags"
            type="text"
            value={tagInput}
            onChange={(e) => setTagInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="‰æã: Ê•ΩÂô®„ÄÅËá™ÁÑ∂"
            className="tag-input"
          />
          <button onClick={addTag} className="add-tag-btn">ËøΩÂä†</button>
        </div>

        {tags.length > 0 && (
          <div className="tags-display">
            {tags.map(tag => (
              <span key={tag} className="tag">
                {tag}
                <button onClick={() => removeTag(tag)} className="remove-tag">√ó</button>
              </span>
            ))}
          </div>
        )}
      </div>

      <div className="editor-actions">
        <button 
          onClick={() => onSave(name, tags)}
          className="button-primary"
          disabled={!name.trim()}
        >
          üíæ ‰øùÂ≠ò
        </button>
        <button onClick={onCancel} className="button-secondary">
          ‚ùå „Ç≠„É£„É≥„Çª„É´
        </button>
      </div>
    </div>
  );
};

