import React, { useState, useRef } from 'react';
import './SoundCollection.css';

const SoundCollection = () => {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [currentRecording, setCurrentRecording] = useState(null);
  const fileInputRef = useRef(null);

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      const chunks = [];

      recorder.ondataavailable = (e) => {
        chunks.push(e.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        const url = URL.createObjectURL(blob);
        setCurrentRecording({
          id: Date.now(),
          url: url,
          audioBlob: blob,
          name: '',
          tags: [],
          createdAt: new Date()
        });
      };

      recorder.start();
      setMediaRecorder(recorder);
      setIsRecording(true);
    } catch (error) {
      console.error('Èå≤Èü≥„ÅÆÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
      alert('„Éû„Ç§„ÇØ„Å∏„ÅÆ„Ç¢„ÇØ„Çª„Çπ„ÅåË®±ÂèØ„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ');
    }
  };

  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.stop();
      mediaRecorder.stream.getTracks().forEach(track => track.stop());
      setIsRecording(false);
      setMediaRecorder(null);
    }
  };

  // Blob„Çí Base64 „Å´Â§âÊèõ„Åô„ÇãÈñ¢Êï∞
  const blobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });
  };

  const saveRecording = async (name, tags) => {
    if (currentRecording && name.trim()) {
      try {
        // Blob„Çíbase64„Å´Â§âÊèõ
        const base64Data = await blobToBase64(currentRecording.audioBlob);

        const savedRecording = {
          ...currentRecording,
          name: name.trim(),
          tags: tags.filter(tag => tag.trim()).map(tag => tag.trim()),
          audioData: base64Data, // base64„Éá„Éº„Çø„Çí‰øùÂ≠ò
          // audioBlob„ÅØ‰∏ÄÊôÇÁöÑ„Å™„ÇÇ„ÅÆ„Å™„ÅÆ„ÅßÂâäÈô§
          audioBlob: undefined
        };

        setRecordings([...recordings, savedRecording]);
        setCurrentRecording(null);

        // LocalStorage„Å´‰øùÂ≠òÔºàaudioBlob„ÅØÈô§Â§ñÔºâ
        const existingRecordings = JSON.parse(localStorage.getItem('soundRecordings') || '[]');
        const recordingToSave = { ...savedRecording };
        delete recordingToSave.audioBlob; // Blob„ÅØ‰øùÂ≠ò„Åó„Å™„ÅÑ
        localStorage.setItem('soundRecordings', JSON.stringify([...existingRecordings, recordingToSave]));
      } catch (error) {
        console.error('Èå≤Èü≥„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
        alert('Èå≤Èü≥„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇÂÜçÂ∫¶„ÅäË©¶„Åó„Åè„Å†„Åï„ÅÑ„ÄÇ');
      }
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('audio/')) {
      const url = URL.createObjectURL(file);
      setCurrentRecording({
        id: Date.now(),
        url: url,
        audioBlob: file,
        name: file.name.replace(/\.[^/.]+$/, ''),
        tags: [],
        createdAt: new Date()
      });
    }
  };

  return (
    <div className="sound-collection">
      <h2>üé§ Èü≥„ÅÇ„Å§„ÇÅ„Éö„Éº„Ç∏</h2>
      <p>Ë∫´„ÅÆÂõû„Çä„Å´„ÅÇ„ÇãÈü≥„ÇíÈå≤Èü≥„Åó„Åü„Çä„ÄÅÈü≥„Éï„Ç°„Ç§„É´„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶Èü≥Á¥†Êùê„ÇíÈõÜ„ÇÅ„Åæ„Åó„Çá„ÅÜÔºÅ</p>

      <div className="collection-actions">
        <div className="recording-section card">
          <h3>üéôÔ∏è Èü≥„ÇíÈå≤Èü≥„Åô„Çã</h3>
          <div className="recording-controls">
            {!isRecording ? (
              <button 
                className="button-primary large-button record-btn"
                onClick={startRecording}
              >
                üî¥ Èå≤Èü≥ÈñãÂßã
              </button>
            ) : (
              <button 
                className="button-secondary large-button stop-btn"
                onClick={stopRecording}
              >
                ‚èπÔ∏è Èå≤Èü≥ÂÅúÊ≠¢
              </button>
            )}
          </div>
          {isRecording && (
            <div className="recording-indicator">
              <div className="pulse-dot"></div>
              Èå≤Èü≥‰∏≠...
            </div>
          )}
        </div>

        <div className="upload-section card">
          <h3>üìÅ Èü≥„Éï„Ç°„Ç§„É´„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ</h3>
          <button 
            className="button-secondary large-button"
            onClick={() => fileInputRef.current?.click()}
          >
            üìÇ „Éï„Ç°„Ç§„É´„ÇíÈÅ∏Êäû
          </button>
          <input
            ref={fileInputRef}
            type="file"
            accept="audio/*"
            onChange={handleFileUpload}
            style={{ display: 'none' }}
          />
        </div>
      </div>

      {currentRecording && (
        <RecordingEditor 
          recording={currentRecording}
          onSave={saveRecording}
          onCancel={() => setCurrentRecording(null)}
        />
      )}

      <div className="recent-recordings">
        <h3>üìù ÊúÄËøëÈå≤Èü≥„Åó„ÅüÈü≥</h3>
        {recordings.length === 0 ? (
          <p className="no-recordings">„Åæ„Å†Èå≤Èü≥„Åó„ÅüÈü≥„Åå„ÅÇ„Çä„Åæ„Åõ„Çì„ÄÇ‰∏ä„ÅÆÈå≤Èü≥„Éú„Çø„É≥„Åã„ÇâÂßã„ÇÅ„Åæ„Åó„Çá„ÅÜÔºÅ</p>
        ) : (
          <div className="recordings-grid">
            {recordings.map(recording => (
              <SoundCard key={recording.id} recording={recording} />
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const RecordingEditor = ({ recording, onSave, onCancel }) => {
  const [name, setName] = useState(recording.name);
  const [tagInput, setTagInput] = useState('');
  const [tags, setTags] = useState(recording.tags);

  const addTag = () => {
    if (tagInput.trim() && !tags.includes(tagInput.trim())) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
    }
  };

  const removeTag = (tagToRemove) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      addTag();
    }
  };

  return (
    <div className="recording-editor card">
      <h3>‚úèÔ∏è Èü≥„Å´ÂêçÂâç„Çí„Å§„Åë„Çà„ÅÜ</h3>

      <audio controls src={recording.url} className="audio-preview" />

      <div className="form-group">
        <label htmlFor="soundName">Èü≥„ÅÆÂêçÂâç:</label>
        <input
          id="soundName"
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="‰æã: „Éî„Ç¢„Éé„ÅÆÈü≥„ÄÅÈõ®„ÅÆÈü≥"
          className="sound-name-input"
        />
      </div>

      <div className="form-group">
        <label htmlFor="soundTags">„Çø„Ç∞:</label>
        <div className="tag-input-container">
          <input
            id="soundTags"
            type="text"
            value={tagInput}
            onChange={(e) => setTagInput(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="‰æã: Ê•ΩÂô®„ÄÅËá™ÁÑ∂"
            className="tag-input"
          />
          <button onClick={addTag} className="add-tag-btn">ËøΩÂä†</button>
        </div>

        {tags.length > 0 && (
          <div className="tags-display">
            {tags.map(tag => (
              <span key={tag} className="tag">
                {tag}
                <button onClick={() => removeTag(tag)} className="remove-tag">√ó</button>
              </span>
            ))}
          </div>
        )}
      </div>

      <div className="editor-actions">
        <button 
          onClick={() => onSave(name, tags)}
          className="button-primary"
          disabled={!name.trim()}
        >
          üíæ ‰øùÂ≠ò
        </button>
        <button onClick={onCancel} className="button-secondary">
          ‚ùå „Ç≠„É£„É≥„Çª„É´
        </button>
      </div>
    </div>
  );
};

const SoundCard = ({ recording }) => {
  return (
    <div className="sound-card">
      <div className="sound-info">
        <h4>{recording.name}</h4>
        <p className="sound-date">
          {new Date(recording.createdAt).toLocaleDateString('ja-JP')}
        </p>
        {recording.tags.length > 0 && (
          <div className="sound-tags">
            {recording.tags.map(tag => (
              <span key={tag} className="tag small">{tag}</span>
            ))}
          </div>
        )}
      </div>
      <audio controls src={recording.url} />
    </div>
  );
};

export default SoundCollection;
