import React, { useState, useRef, useEffect } from 'react';
import './DAWPage.css';

const DAWPage = () => {
  const [tracks, setTracks] = useState([{ id: 1, name: '„Éà„É©„ÉÉ„ÇØ 1', clips: [] }]);
  const [bpm, setBpm] = useState(120);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [audioContext, setAudioContext] = useState(null);
  const [trackHeight] = useState(80);
  const [playingAudios, setPlayingAudios] = useState(new Map());
  const [startPlayTime, setStartPlayTime] = useState(null);
  const timelineRef = useRef(null);
  const playheadRef = useRef(null);
  const animationFrameRef = useRef(null);

  useEffect(() => {
    // Web Audio API „ÅÆÂàùÊúüÂåñ
    const ctx = new (window.AudioContext || window.webkitAudioContext)();
    setAudioContext(ctx);

    return () => {
      if (ctx) {
        ctx.close();
      }
      if (animationFrameRef.current) {
        cancelAnimationFrame(animationFrameRef.current);
      }
    };
  }, []);

  // Èü≥Â£∞„Éï„Ç°„Ç§„É´„ÅÆÁ∂ôÁ∂öÊôÇÈñì„ÇíÂèñÂæó
  const getAudioDuration = (audioBlob) => {
    return new Promise((resolve) => {
      const audio = new Audio();
      audio.addEventListener('loadedmetadata', () => {
        resolve(audio.duration * 100); // Áßí„Åã„Çâ„Éî„ÇØ„Çª„É´„Å´Â§âÊèõÔºà1Áßí=100pxÔºâ
      });
      audio.addEventListener('error', () => {
        resolve(200); // „Ç®„É©„Éº„ÅÆÂ†¥Âêà„ÅØ„Éá„Éï„Ç©„É´„ÉàÂÄ§
      });
      audio.src = URL.createObjectURL(audioBlob);
    });
  };

  // „Éó„É¨„Ç§„Éò„ÉÉ„Éâ„ÅÆ„Ç¢„Éã„É°„Éº„Ç∑„Éß„É≥Êõ¥Êñ∞
  const updatePlayhead = () => {
    if (isPlaying && startPlayTime) {
      const elapsed = (Date.now() - startPlayTime) / 1000; // ÁµåÈÅéÊôÇÈñìÔºàÁßíÔºâ
      const pixelsPerSecond = (bpm / 60) * 100; // BPM„Å´Âü∫„Å•„ÅÑ„Åü„Éî„ÇØ„Çª„É´/Áßí
      setCurrentTime(elapsed * pixelsPerSecond);
      animationFrameRef.current = requestAnimationFrame(updatePlayhead);
    }
  };

  useEffect(() => {
    if (isPlaying) {
