import React, { useState, useRef, useEffect } from 'react';
import './SoundCollection.css';
import { useAnnouncement, useErrorMessages } from '../hooks/useAccessibility';

const SoundCollection = () => {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [currentRecording, setCurrentRecording] = useState(null);
  const fileInputRef = useRef(null);
  const recordButtonRef = useRef(null);

  // „Ç¢„ÇØ„Çª„Ç∑„Éì„É™„ÉÜ„Ç£„Éï„ÉÉ„ÇØ
  const { announce, AnnouncementRegion } = useAnnouncement();
  const { showError, clearError, ErrorRegion } = useErrorMessages();

  // „Ç≥„É≥„Éù„Éº„Éç„É≥„Éà„Ç¢„É≥„Éû„Ç¶„É≥„ÉàÊôÇ„ÅÆ„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó
  useEffect(() => {
    return () => {
      // Èå≤Èü≥‰∏≠„ÅÆÂ†¥Âêà„ÅØÂÅúÊ≠¢
      if (mediaRecorder && isRecording) {
        mediaRecorder.stop();
        if (mediaRecorder.stream) {
          mediaRecorder.stream.getTracks().forEach(track => track.stop());
        }
      }

      // ‰ΩúÊàê„Åï„Çå„ÅüBlobURL„Çí„ÇØ„É™„Éº„É≥„Ç¢„ÉÉ„Éó
      recordings.forEach(recording => {
        if (recording.url && recording.url.startsWith('blob:')) {
          URL.revokeObjectURL(recording.url);
        }
      });
    };
  }, [mediaRecorder, isRecording, recordings]);

  const startRecording = async () => {
    try {
      // „Ç®„É©„Éº„É°„ÉÉ„Çª„Éº„Ç∏„Çí„ÇØ„É™„Ç¢
      clearError();
      announce('Èå≤Èü≥„ÇíÈñãÂßã„Åó„Å¶„ÅÑ„Åæ„Åô...', 'assertive');

      // iOS„Åß„ÅÆ„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„ÇπÊîπÂñÑ - „Åæ„Åö„ÉÜ„Çπ„Éà„ÇíÂÆüË°å
      const hasAccess = await testMicrophoneAccess();
      if (!hasAccess) {
        showError('„Éû„Ç§„ÇØ„Å´„Ç¢„ÇØ„Çª„Çπ„Åß„Åç„Åæ„Åõ„Çì„Åß„Åó„Åü„ÄÇ„Éñ„É©„Ç¶„Ç∂„ÅÆË®≠ÂÆö„ÇíÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ');
        return;
      }

      console.log('Èå≤Èü≥ÈñãÂßãÂá¶ÁêÜ‰∏≠...');

      // iPadÁî®„ÅÆÈü≥Â£∞Ë®≠ÂÆö„ÇíÊúÄÈÅ©Âåñ
      const audioConstraints = {
        echoCancellation: false, // iPad„Åß„ÅØÁÑ°Âäπ„Å´„Åô„Çã
        noiseSuppression: false, // iPad„Åß„ÅØÁÑ°Âäπ„Å´„Åô„Çã  
        autoGainControl: false,  // iPad„Åß„ÅØÁÑ°Âäπ„Å´„Åô„Çã
        sampleRate: 44100,       // ÊòéÁ§∫ÁöÑ„Å´„Çµ„É≥„Éó„É´„É¨„Éº„Éà„ÇíÊåáÂÆö
        channelCount: 1          // „É¢„Éé„É©„É´Èå≤Èü≥„ÇíÊòéÁ§∫
      };

      console.log('Èü≥Â£∞Âà∂Á¥Ñ:', audioConstraints);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: audioConstraints
      });

      console.log('Èå≤Èü≥„Çπ„Éà„É™„Éº„É†ÂèñÂæóÊàêÂäü');
      console.log('„Çπ„Éà„É™„Éº„É†Ë©≥Á¥∞:', {
        id: stream.id,
        active: stream.active,
        tracks: stream.getTracks().map(track => ({
          id: track.id,
          kind: track.kind,
          label: track.label,
          enabled: track.enabled,
          muted: track.muted,
          readyState: track.readyState,
          settings: track.getSettings ? track.getSettings() : 'getSettings not supported'
        }))
      });

      // MediaRecorder„ÅÆ„Ç™„Éó„Ç∑„Éß„É≥„ÇíÊ±∫ÂÆö
      let recorderOptions = {};

      if (MediaRecorder.isTypeSupported('audio/webm;codecs=opus')) {
        recorderOptions.mimeType = 'audio/webm;codecs=opus';
        console.log('‰ΩøÁî®„Åô„ÇãMIME„Çø„Ç§„Éó: audio/webm;codecs=opus');
      } else if (MediaRecorder.isTypeSupported('audio/mp4')) {
        recorderOptions.mimeType = 'audio/mp4';
        console.log('‰ΩøÁî®„Åô„ÇãMIME„Çø„Ç§„Éó: audio/mp4');
      } else if (MediaRecorder.isTypeSupported('audio/wav')) {
        recorderOptions.mimeType = 'audio/wav';
        console.log('‰ΩøÁî®„Åô„ÇãMIME„Çø„Ç§„Éó: audio/wav');
      } else {
        console.log('„Éá„Éï„Ç©„É´„ÉàMIME„Çø„Ç§„Éó„Çí‰ΩøÁî®');
      }

      const recorder = new MediaRecorder(stream, recorderOptions);
      console.log('MediaRecorder‰ΩúÊàê:', {
        mimeType: recorder.mimeType,
        state: recorder.state,
        supportedTypes: {
          'audio/webm;codecs=opus': MediaRecorder.isTypeSupported('audio/webm;codecs=opus'),
          'audio/mp4': MediaRecorder.isTypeSupported('audio/mp4'),
          'audio/wav': MediaRecorder.isTypeSupported('audio/wav')
        }
      });

      const chunks = [];

      recorder.ondataavailable = (e) => {
        console.log('Èå≤Èü≥„Éá„Éº„ÇøÂèó‰ø°:', e.data.size, 'bytes, type:', e.data.type);
        chunks.push(e.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        const url = URL.createObjectURL(blob);
        console.log('Èå≤Èü≥ÂÆå‰∫Ü - Blob„Çµ„Ç§„Ç∫:', blob.size, 'bytes, type:', blob.type);
        console.log('„ÉÅ„É£„É≥„ÇØÊï∞:', chunks.length, 'chunks');
        setCurrentRecording({
          id: Date.now(),
          url: url,
          audioBlob: blob,
          name: '',
          tags: [],
          createdAt: new Date()
        });

        // Èü≥Â£∞„É¨„Éô„É´Áõ£Ë¶ñÂÅúÊ≠¢
        setAudioLevel(0);
        if (animationFrameRef.current) {
          cancelAnimationFrame(animationFrameRef.current);
        }

        announce('Èå≤Èü≥„ÅåÂÆå‰∫Ü„Åó„Åæ„Åó„Åü„ÄÇÈü≥„Å´ÂêçÂâç„Çí„Å§„Åë„Å¶‰øùÂ≠ò„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ', 'assertive');
      };

      recorder.start();
      console.log('Èå≤Èü≥ÈñãÂßã:', recorder.state);
      setMediaRecorder(recorder);
      setIsRecording(true);
      announce('Èå≤Èü≥„ÇíÈñãÂßã„Åó„Åæ„Åó„Åü„ÄÇ', 'assertive');
      console.log('Èå≤Èü≥ÈñãÂßãÂÆå‰∫Ü');

    } catch (error) {
      console.error('Èå≤Èü≥„ÅÆÈñãÂßã„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);

      let errorMessage = 'Èå≤Èü≥„ÇíÈñãÂßã„Åß„Åç„Åæ„Åõ„Çì„Åß„Åó„Åü„ÄÇ';

      if (error.name === 'NotAllowedError') {
        errorMessage = '„Éû„Ç§„ÇØ„ÅÆ‰ΩøÁî®„ÅåÊãíÂê¶„Åï„Çå„Åæ„Åó„Åü„ÄÇ„Éñ„É©„Ç¶„Ç∂„ÅÆË®≠ÂÆö„Åß„Éû„Ç§„ÇØ„Ç¢„ÇØ„Çª„Çπ„ÇíË®±ÂèØ„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ';
      } else if (error.name === 'NotFoundError') {
        errorMessage = '„Éû„Ç§„ÇØ„ÅåË¶ã„Å§„Åã„Çä„Åæ„Åõ„Çì„ÄÇ„Éá„Éê„Ç§„Çπ„Å´„Éû„Ç§„ÇØ„ÅåÊé•Á∂ö„Åï„Çå„Å¶„ÅÑ„Çã„ÅãÁ¢∫Ë™ç„Åó„Å¶„Åè„Å†„Åï„ÅÑ„ÄÇ';
      } else if (error.name === 'NotSupportedError') {
        errorMessage = '„Åä‰Ωø„ÅÑ„ÅÆ„Éñ„É©„Ç¶„Ç∂„ÅØÈå≤Èü≥Ê©üËÉΩ„Çí„Çµ„Éù„Éº„Éà„Åó„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ';
      } else if (error.name === 'NotReadableError') {
        errorMessage = '„Éû„Ç§„ÇØ„Åå‰ªñ„ÅÆ„Ç¢„Éó„É™„Ç±„Éº„Ç∑„Éß„É≥„Åß‰ΩøÁî®‰∏≠„ÅÆÂèØËÉΩÊÄß„Åå„ÅÇ„Çä„Åæ„Åô„ÄÇ';
      } else if (error.message) {
        errorMessage = error.message;
      }

      showError(errorMessage);
      announce(errorMessage, 'assertive');
    }
  };

  const stopRecording = () => {
    console.log('Èå≤Èü≥ÂÅúÊ≠¢ÈñãÂßã');
    setIsRecording(false); // „Åæ„ÅöÈå≤Èü≥Áä∂ÊÖã„ÇíÂÅúÊ≠¢„Å´Ë®≠ÂÆö

    if (mediaRecorder) {
      mediaRecorder.stop();
      mediaRecorder.stream.getTracks().forEach(track => track.stop());
      setMediaRecorder(null);
      announce('Èå≤Èü≥„ÇíÂÅúÊ≠¢„Åó„Åæ„Åó„Åü„ÄÇ', 'assertive');
    }

    console.log('Èå≤Èü≥ÂÅúÊ≠¢ÂÆå‰∫Ü');
  };

  // Blob„Çí Base64 „Å´Â§âÊèõ„Åô„ÇãÈñ¢Êï∞
  const blobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });
  };

  const saveRecording = async (name, tags) => {
    if (currentRecording && name.trim()) {
      try {
        // Blob„Çíbase64„Å´Â§âÊèõ
        const base64Data = await blobToBase64(currentRecording.audioBlob);

        const savedRecording = {
          ...currentRecording,
          name: name.trim(),
          tags: tags.filter(tag => tag.trim()).map(tag => tag.trim()),
          audioData: base64Data, // base64„Éá„Éº„Çø„Çí‰øùÂ≠ò
          // audioBlob„ÅØ‰∏ÄÊôÇÁöÑ„Å™„ÇÇ„ÅÆ„Å™„ÅÆ„ÅßÂâäÈô§
          audioBlob: undefined
        };

        setRecordings([...recordings, savedRecording]);
        setCurrentRecording(null);

        // LocalStorage„Å´‰øùÂ≠òÔºàaudioBlob„ÅØÈô§Â§ñÔºâ
        const existingRecordings = JSON.parse(localStorage.getItem('soundRecordings') || '[]');
        const recordingToSave = { ...savedRecording };
        delete recordingToSave.audioBlob; // Blob„ÅØ‰øùÂ≠ò„Åó„Å™„ÅÑ
        localStorage.setItem('soundRecordings', JSON.stringify([...existingRecordings, recordingToSave]));
      } catch (error) {
        console.error('Èå≤Èü≥„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü:', error);
        alert('Èå≤Èü≥„ÅÆ‰øùÂ≠ò„Å´Â§±Êïó„Åó„Åæ„Åó„Åü„ÄÇÂÜçÂ∫¶„ÅäË©¶„Åó„Åè„Å†„Åï„ÅÑ„ÄÇ');
      }
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('audio/')) {
      const url = URL.createObjectURL(file);
      setCurrentRecording({
        id: Date.now(),
