};

const RecordingEditor = ({ recording, onSave, onCancel }) => {
  const [name, setName] = useState(recording.name);
  const [tagInput, setTagInput] = useState('');
  const [tags, setTags] = useState(recording.tags);
  const [validationMessage, setValidationMessage] = useState('');

  const addTag = () => {
    if (tagInput.trim() && !tags.includes(tagInput.trim())) {
      setTags([...tags, tagInput.trim()]);
      setTagInput('');
      setValidationMessage(`„Çø„Ç∞„Äå${tagInput.trim()}„Äç„ÇíËøΩÂä†„Åó„Åæ„Åó„Åü`);
    } else if (tags.includes(tagInput.trim())) {
      setValidationMessage('„Åì„ÅÆ„Çø„Ç∞„ÅØÊó¢„Å´ËøΩÂä†„Åï„Çå„Å¶„ÅÑ„Åæ„Åô');
    }
  };

  const removeTag = (tagToRemove) => {
    setTags(tags.filter(tag => tag !== tagToRemove));
    setValidationMessage(`„Çø„Ç∞„Äå${tagToRemove}„Äç„ÇíÂâäÈô§„Åó„Åæ„Åó„Åü`);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      addTag();
    }
  };

  const handleSave = () => {
    if (!name.trim()) {
      setValidationMessage('Èü≥„ÅÆÂêçÂâç„ÇíÂÖ•Âäõ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }
    onSave(name, tags);
  };

  return (
    <section 
      className="recording-editor card" 
      role="dialog" 
      aria-labelledby="editor-title"
      aria-describedby="editor-description"
    >
      <h3 id="editor-title">
        <span role="img" aria-label="Á∑®ÈõÜ">‚úèÔ∏è</span> Èü≥„Å´ÂêçÂâç„Çí„Å§„Åë„Çà„ÅÜ
      </h3>
      <p id="editor-description" className="sr-only">
        Èå≤Èü≥„Åó„ÅüÈü≥„Å´ÂêçÂâç„Å®„Çø„Ç∞„Çí„Å§„Åë„Å¶‰øùÂ≠ò„Åß„Åç„Åæ„Åô
      </p>

      <div className="audio-preview-container">
        <label htmlFor="audio-preview" className="audio-preview-label">Èå≤Èü≥„Åó„ÅüÈü≥„ÅÆ„Éó„É¨„Éì„É•„Éº:</label>
        <audio 
          id="audio-preview"
          controls 
          src={recording.url} 
          className="accessible-audio audio-preview"
          onError={(e) => {
            console.error('Èü≥Â£∞„Éó„É¨„Éì„É•„Éº„ÅÆË™≠„ÅøËæº„Åø„Ç®„É©„Éº:', e);
          }}
          aria-describedby="audio-preview-desc"
        >
          <track kind="captions" label="Èü≥Â£∞Ë™¨Êòé" srcLang="ja" />
          „Åä‰Ωø„ÅÑ„ÅÆ„Éñ„É©„Ç¶„Ç∂„ÅØÈü≥Â£∞ÂÜçÁîü„Å´ÂØæÂøú„Åó„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ
        </audio>
        <p id="audio-preview-desc" className="sr-only">
          Èå≤Èü≥„Åï„Çå„ÅüÈü≥Â£∞„ÇíÂÜçÁîü„Åó„Å¶Á¢∫Ë™ç„Åß„Åç„Åæ„Åô
        </p>
      </div>

      <div className="form-group">
        <label htmlFor="soundName" className="required-label">
          Èü≥„ÅÆÂêçÂâç <span aria-label="ÂøÖÈ†à" className="required">*</span>:
        </label>
        <input
