import React, { useState, useRef, useEffect } from 'react';
import './SoundCollection.css';
import { useAnnouncement, useErrorMessages, useProgress } from '../hooks/useAccessibility';

const SoundCollection = () => {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [currentRecording, setCurrentRecording] = useState(null);
  const [audioLevel, setAudioLevel] = useState(0);
  const [audioContext, setAudioContext] = useState(null);
  const fileInputRef = useRef(null);
  const animationFrameRef = useRef(null);
  const recordButtonRef = useRef(null);

  // ã‚¢ã‚¯ã‚»ã‚·ãƒ“ãƒªãƒ†ã‚£ãƒ•ãƒƒã‚¯
  const { announce, AnnouncementRegion } = useAnnouncement();
  const { showError, clearError, ErrorRegion } = useErrorMessages();
  const { updateProgress, ProgressBar } = useProgress();

  // ã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆã‚¢ãƒ³ãƒã‚¦ãƒ³ãƒˆæ™‚ã®ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
  useEffect(() => {
    return () => {
      // éŒ²éŸ³ä¸­ã®å ´åˆã¯åœæ­¢
      if (mediaRecorder && isRecording) {
        mediaRecorder.stop();
        if (mediaRecorder.stream) {
          mediaRecorder.stream.getTracks().forEach(track => track.stop());
        }
      }

      // ä½œæˆã•ã‚ŒãŸBlobURLã‚’ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
      recordings.forEach(recording => {
        if (recording.url && recording.url.startsWith('blob:')) {
          URL.revokeObjectURL(recording.url);
        }
      });
    };
  }, [mediaRecorder, isRecording, recordings]);

  const startRecording = async () => {
    try {
      // ã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’ã‚¯ãƒªã‚¢
      clearError();
      announce('éŒ²éŸ³ã‚’é–‹å§‹ã—ã¦ã„ã¾ã™...', 'assertive');

      // iOSã§ã®ãƒã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹æ”¹å–„ - ã¾ãšãƒ†ã‚¹ãƒˆã‚’å®Ÿè¡Œ
      const hasAccess = await testMicrophoneAccess();
      if (!hasAccess) {
        showError('ãƒã‚¤ã‚¯ã«ã‚¢ã‚¯ã‚»ã‚¹ã§ãã¾ã›ã‚“ã§ã—ãŸã€‚ãƒ–ãƒ©ã‚¦ã‚¶ã®è¨­å®šã‚’ç¢ºèªã—ã¦ãã ã•ã„ã€‚');
        return;
      }

      console.log('éŒ²éŸ³é–‹å§‹å‡¦ç†ä¸­...');

      // iPadç”¨ã®éŸ³å£°è¨­å®šã‚’æœ€é©åŒ–
      const audioConstraints = {
        echoCancellation: false, // iPadã§ã¯ç„¡åŠ¹ã«ã™ã‚‹
        noiseSuppression: false, // iPadã§ã¯ç„¡åŠ¹ã«ã™ã‚‹  
        autoGainControl: false,  // iPadã§ã¯ç„¡åŠ¹ã«ã™ã‚‹
        sampleRate: 44100,       // æ˜ç¤ºçš„ã«ã‚µãƒ³ãƒ—ãƒ«ãƒ¬ãƒ¼ãƒˆã‚’æŒ‡å®š
        channelCount: 1          // ãƒ¢ãƒãƒ©ãƒ«éŒ²éŸ³ã‚’æ˜ç¤º
      };

      console.log('éŸ³å£°åˆ¶ç´„:', audioConstraints);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: audioConstraints
      });

      console.log('éŒ²éŸ³ã‚¹ãƒˆãƒªãƒ¼ãƒ å–å¾—æˆåŠŸ');
      console.log('ã‚¹ãƒˆãƒªãƒ¼ãƒ è©³ç´°:', {
        id: stream.id,
        active: stream.active,
        tracks: stream.getTracks().map(track => ({
          id: track.id,
          kind: track.kind,
          label: track.label,
          enabled: track.enabled,
          muted: track.muted,
          readyState: track.readyState,
          settings: track.getSettings ? track.getSettings() : 'getSettings not supported'
        }))
      });

      // éŸ³å£°ãƒ¬ãƒ™ãƒ«ç›£è¦–ã®ãŸã‚ã®AudioContextã‚’è¨­å®š
      const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
      console.log('AudioContextä½œæˆ:', {
        state: audioCtx.state,
        sampleRate: audioCtx.sampleRate,
        baseLatency: audioCtx.baseLatency
      });

      // AudioContextãŒåœæ­¢ã—ã¦ã„ã‚‹å ´åˆã¯å†é–‹
      if (audioCtx.state === 'suspended') {
        console.log('AudioContextã‚’å†é–‹ä¸­...');
        await audioCtx.resume();
        console.log('AudioContextå†é–‹å®Œäº†:', audioCtx.state);
      }

      const source = audioCtx.createMediaStreamSource(stream);
      const analyserNode = audioCtx.createAnalyser();
      analyserNode.fftSize = 256;
      analyserNode.smoothingTimeConstant = 0.8; // ã‚¹ãƒ ãƒ¼ã‚¸ãƒ³ã‚°ã‚’è¿½åŠ 
      source.connect(analyserNode);

      console.log('AudioContextæ¥ç¶šå®Œäº†:', {
        analyserFFTSize: analyserNode.fftSize,
        frequencyBinCount: analyserNode.frequencyBinCount,
