      setMediaRecorder(recorder);
      setIsRecording(true);
      announce('éŒ²éŸ³ã‚’é–‹å§‹ã—ã¾ã—ãŸã€‚', 'assertive');
      console.log('éŒ²éŸ³é–‹å§‹å®Œäº†');

    } catch (error) {
