  // ãƒã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ãƒ†ã‚¹ãƒˆæ©Ÿèƒ½ï¼ˆiOSã§ã®å•é¡Œå¯¾ç­–ï¼‰
  const testMicrophoneAccess = async () => {
    try {
      console.log('ãƒã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ãƒ†ã‚¹ãƒˆé–‹å§‹');

      // HTTPSæ¥ç¶šãƒã‚§ãƒƒã‚¯
      if (!checkHTTPS()) {
        alert('ğŸ”’ éŒ²éŸ³æ©Ÿèƒ½ã‚’ä½¿ç”¨ã™ã‚‹ã«ã¯HTTPSæ¥ç¶šãŒå¿…è¦ã§ã™ã€‚\n\niPhoneã§ã¯ç‰¹ã«ã€ã‚»ã‚­ãƒ¥ã‚¢ãªæ¥ç¶šãŒå¿…è¦ã¨ãªã‚Šã¾ã™ã€‚');
        return false;
      }

      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('ãŠä½¿ã„ã®ãƒ–ãƒ©ã‚¦ã‚¶ã¯éŒ²éŸ³æ©Ÿèƒ½ã‚’ã‚µãƒãƒ¼ãƒˆã—ã¦ã„ã¾ã›ã‚“ã€‚');
      }

      // ã¾ãšãƒã‚¤ã‚¯ã®æ¨©é™çŠ¶æ…‹ã‚’ç¢ºèª
      if (navigator.permissions) {
        try {
          const permission = await navigator.permissions.query({ name: 'microphone' });
          console.log('ãƒã‚¤ã‚¯æ¨©é™çŠ¶æ…‹:', permission.state);

          if (permission.state === 'denied') {
            alert('ãƒã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ãŒæ‹’å¦ã•ã‚Œã¦ã„ã¾ã™ã€‚ãƒ–ãƒ©ã‚¦ã‚¶ã®è¨­å®šã‹ã‚‰ãƒã‚¤ã‚¯ã®ä½¿ç”¨ã‚’è¨±å¯ã—ã¦ãã ã•ã„ã€‚');
