  // éŸ³å£°ãƒ¬ãƒ™ãƒ«ç›£è¦–é–¢æ•°
  const monitorAudioLevel = (analyserNode) => {
    const bufferLength = analyserNode.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    console.log('éŸ³å£°ãƒ¬ãƒ™ãƒ«ç›£è¦–é–‹å§‹ - bufferLength:', bufferLength);

    const updateLevel = () => {
      if (!isRecording) {
        console.log('éŒ²éŸ³åœæ­¢ã®ãŸã‚éŸ³å£°ãƒ¬ãƒ™ãƒ«ç›£è¦–çµ‚äº†');
        return;
      }

      // æ™‚é–“é ˜åŸŸãƒ‡ãƒ¼ã‚¿ã‚’å–å¾—ï¼ˆå‘¨æ³¢æ•°é ˜åŸŸã§ã¯ãªãï¼‰
      analyserNode.getByteTimeDomainData(dataArray);

      // ãƒ‡ãƒãƒƒã‚°ç”¨ï¼šæœ€åˆã®10ã‚µãƒ³ãƒ—ãƒ«ã‚’ãƒ­ã‚°å‡ºåŠ›ï¼ˆæœ€åˆã®æ•°ç§’ã®ã¿ï¼‰
      if (Math.random() < 0.01) { // 1%ã®ç¢ºç‡ã§ãƒ­ã‚°å‡ºåŠ›
        console.log('éŸ³å£°ãƒ‡ãƒ¼ã‚¿ã‚µãƒ³ãƒ—ãƒ«:', Array.from(dataArray.slice(0, 10)));
      }

      // RMSï¼ˆäºŒä¹—å¹³å‡å¹³æ–¹æ ¹ï¼‰ã‚’è¨ˆç®—
      let sum = 0;
      for (let i = 0; i < bufferLength; i++) {
        const sample = (dataArray[i] - 128) / 128; // -1 to 1ã®ç¯„å›²ã«æ­£è¦åŒ–
        sum += sample * sample;
      }
      const rms = Math.sqrt(sum / bufferLength);

      // ãƒ‡ã‚·ãƒ™ãƒ«ã«å¤‰æ›ã—ã¦0-100ã®ç¯„å›²ã«ãƒãƒƒãƒ”ãƒ³ã‚°
      const db = 20 * Math.log10(rms + 0.0001); // 0.0001ã¯-âˆã‚’é˜²ããŸã‚
      const level = Math.max(0, Math.min(100, ((db + 60) / 60) * 100)); // -60dBã‚’0%ã€0dBã‚’100%ã«

      // ãƒ‡ãƒãƒƒã‚°ç”¨ï¼šãƒ¬ãƒ™ãƒ«ã‚’ãƒ­ã‚°å‡ºåŠ›ï¼ˆæ™‚ã€…ï¼‰
      if (Math.random() < 0.01) { // 1%ã®ç¢ºç‡ã§ãƒ­ã‚°å‡ºåŠ›
        console.log('éŸ³å£°ãƒ¬ãƒ™ãƒ« - RMS:', rms, 'dB:', db, 'Level:', level);
      }

      setAudioLevel(level);

      if (isRecording) {
        animationFrameRef.current = requestAnimationFrame(updateLevel);
      }
    };

    updateLevel();
  };

  return (
