import React, { useState, useRef, useEffect } from 'react';
import './SoundCollection.css';
import { useAnnouncement, useErrorMessages } from '../hooks/useAccessibility';

const SoundCollection = () => {
  const [recordings, setRecordings] = useState([]);
  const [isRecording, setIsRecording] = useState(false);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [currentRecording, setCurrentRecording] = useState(null);
  const fileInputRef = useRef(null);
  const recordButtonRef = useRef(null);

  // ã‚¢ã‚¯ã‚»ã‚·ãƒ“ãƒªãƒ†ã‚£ãƒ•ãƒƒã‚¯
  const { announce, AnnouncementRegion } = useAnnouncement();
  const { showError, clearError, ErrorRegion } = useErrorMessages();

  // ã‚³ãƒ³ãƒãƒ¼ãƒãƒ³ãƒˆã‚¢ãƒ³ãƒžã‚¦ãƒ³ãƒˆæ™‚ã®ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
  useEffect(() => {
    return () => {
      // éŒ²éŸ³ä¸­ã®å ´åˆã¯åœæ­¢
      if (mediaRecorder && isRecording) {
        mediaRecorder.stop();
        if (mediaRecorder.stream) {
          mediaRecorder.stream.getTracks().forEach(track => track.stop());
        }
      }

      // ä½œæˆã•ã‚ŒãŸBlobURLã‚’ã‚¯ãƒªãƒ¼ãƒ³ã‚¢ãƒƒãƒ—
      recordings.forEach(recording => {
        if (recording.url && recording.url.startsWith('blob:')) {
          URL.revokeObjectURL(recording.url);
        }
      });
    };
  }, [mediaRecorder, isRecording, recordings]);

  const startRecording = async () => {
    try {
      // ã‚¨ãƒ©ãƒ¼ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’ã‚¯ãƒªã‚¢
      clearError();
      announce('éŒ²éŸ³ã‚’é–‹å§‹ã—ã¦ã„ã¾ã™...', 'assertive');

      // iOSã§ã®ãƒžã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹æ”¹å–„ - ã¾ãšãƒ†ã‚¹ãƒˆã‚’å®Ÿè¡Œ
      const hasAccess = await testMicrophoneAccess();
      if (!hasAccess) {
        showError('ãƒžã‚¤ã‚¯ã«ã‚¢ã‚¯ã‚»ã‚¹ã§ãã¾ã›ã‚“ã§ã—ãŸã€‚ãƒ–ãƒ©ã‚¦ã‚¶ã®è¨­å®šã‚’ç¢ºèªã—ã¦ãã ã•ã„ã€‚');
        return;
      }

      console.log('éŒ²éŸ³é–‹å§‹å‡¦ç†ä¸­...');

      // iPadç”¨ã®éŸ³å£°è¨­å®šã‚’æœ€é©åŒ–
      const audioConstraints = {
        echoCancellation: false, // iPadã§ã¯ç„¡åŠ¹ã«ã™ã‚‹
        noiseSuppression: false, // iPadã§ã¯ç„¡åŠ¹ã«ã™ã‚‹  
        autoGainControl: false,  // iPadã§ã¯ç„¡åŠ¹ã«ã™ã‚‹
        sampleRate: 44100,       // æ˜Žç¤ºçš„ã«ã‚µãƒ³ãƒ—ãƒ«ãƒ¬ãƒ¼ãƒˆã‚’æŒ‡å®š
        channelCount: 1          // ãƒ¢ãƒŽãƒ©ãƒ«éŒ²éŸ³ã‚’æ˜Žç¤º
      };

      console.log('éŸ³å£°åˆ¶ç´„:', audioConstraints);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: audioConstraints
      });

      console.log('éŒ²éŸ³ã‚¹ãƒˆãƒªãƒ¼ãƒ å–å¾—æˆåŠŸ');
      console.log('ã‚¹ãƒˆãƒªãƒ¼ãƒ è©³ç´°:', {
        id: stream.id,
        active: stream.active,
        tracks: stream.getTracks().map(track => ({
          id: track.id,
          kind: track.kind,
          label: track.label,
          enabled: track.enabled,
          muted: track.muted,
          readyState: track.readyState,
          settings: track.getSettings ? track.getSettings() : 'getSettings not supported'
        }))
      });

      // MediaRecorderã®ã‚ªãƒ—ã‚·ãƒ§ãƒ³ã‚’æ±ºå®š
      let recorderOptions = {};

      if (MediaRecorder.isTypeSupported('audio/webm;codecs=opus')) {
        recorderOptions.mimeType = 'audio/webm;codecs=opus';
        console.log('ä½¿ç”¨ã™ã‚‹MIMEã‚¿ã‚¤ãƒ—: audio/webm;codecs=opus');
      } else if (MediaRecorder.isTypeSupported('audio/mp4')) {
        recorderOptions.mimeType = 'audio/mp4';
        console.log('ä½¿ç”¨ã™ã‚‹MIMEã‚¿ã‚¤ãƒ—: audio/mp4');
      } else if (MediaRecorder.isTypeSupported('audio/wav')) {
        recorderOptions.mimeType = 'audio/wav';
        console.log('ä½¿ç”¨ã™ã‚‹MIMEã‚¿ã‚¤ãƒ—: audio/wav');
      } else {
        console.log('ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆMIMEã‚¿ã‚¤ãƒ—ã‚’ä½¿ç”¨');
      }

      const recorder = new MediaRecorder(stream, recorderOptions);
      console.log('MediaRecorderä½œæˆ:', {
        mimeType: recorder.mimeType,
        state: recorder.state,
        supportedTypes: {
          'audio/webm;codecs=opus': MediaRecorder.isTypeSupported('audio/webm;codecs=opus'),
          'audio/mp4': MediaRecorder.isTypeSupported('audio/mp4'),
          'audio/wav': MediaRecorder.isTypeSupported('audio/wav')
        }
      });

      const chunks = [];

      recorder.ondataavailable = (e) => {
        console.log('éŒ²éŸ³ãƒ‡ãƒ¼ã‚¿å—ä¿¡:', e.data.size, 'bytes, type:', e.data.type);
        chunks.push(e.data);
      };

      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        const url = URL.createObjectURL(blob);
        console.log('éŒ²éŸ³å®Œäº† - Blobã‚µã‚¤ã‚º:', blob.size, 'bytes, type:', blob.type);
        console.log('ãƒãƒ£ãƒ³ã‚¯æ•°:', chunks.length, 'chunks');
        setCurrentRecording({
          id: Date.now(),
          url: url,
          audioBlob: blob,
          name: '',
          tags: [],
          createdAt: new Date()
        });

        announce('éŒ²éŸ³ãŒå®Œäº†ã—ã¾ã—ãŸã€‚éŸ³ã«åå‰ã‚’ã¤ã‘ã¦ä¿å­˜ã—ã¦ãã ã•ã„ã€‚', 'assertive');
      };

      recorder.start();
      console.log('éŒ²éŸ³é–‹å§‹:', recorder.state);
      setMediaRecorder(recorder);
      setIsRecording(true);
      announce('éŒ²éŸ³ã‚’é–‹å§‹ã—ã¾ã—ãŸã€‚', 'assertive');
      console.log('éŒ²éŸ³é–‹å§‹å®Œäº†');

    } catch (error) {
      console.error('éŒ²éŸ³ã®é–‹å§‹ã«å¤±æ•—ã—ã¾ã—ãŸ:', error);

      let errorMessage = 'éŒ²éŸ³ã‚’é–‹å§‹ã§ãã¾ã›ã‚“ã§ã—ãŸã€‚';

      if (error.name === 'NotAllowedError') {
        errorMessage = 'ãƒžã‚¤ã‚¯ã®ä½¿ç”¨ãŒæ‹’å¦ã•ã‚Œã¾ã—ãŸã€‚ãƒ–ãƒ©ã‚¦ã‚¶ã®è¨­å®šã§ãƒžã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ã‚’è¨±å¯ã—ã¦ãã ã•ã„ã€‚';
      } else if (error.name === 'NotFoundError') {
        errorMessage = 'ãƒžã‚¤ã‚¯ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“ã€‚ãƒ‡ãƒã‚¤ã‚¹ã«ãƒžã‚¤ã‚¯ãŒæŽ¥ç¶šã•ã‚Œã¦ã„ã‚‹ã‹ç¢ºèªã—ã¦ãã ã•ã„ã€‚';
      } else if (error.name === 'NotSupportedError') {
        errorMessage = 'ãŠä½¿ã„ã®ãƒ–ãƒ©ã‚¦ã‚¶ã¯éŒ²éŸ³æ©Ÿèƒ½ã‚’ã‚µãƒãƒ¼ãƒˆã—ã¦ã„ã¾ã›ã‚“ã€‚';
      } else if (error.name === 'NotReadableError') {
        errorMessage = 'ãƒžã‚¤ã‚¯ãŒä»–ã®ã‚¢ãƒ—ãƒªã‚±ãƒ¼ã‚·ãƒ§ãƒ³ã§ä½¿ç”¨ä¸­ã®å¯èƒ½æ€§ãŒã‚ã‚Šã¾ã™ã€‚';
      } else if (error.message) {
        errorMessage = error.message;
      }

      showError(errorMessage);
      announce(errorMessage, 'assertive');
    }
  };

  const stopRecording = () => {
    console.log('éŒ²éŸ³åœæ­¢é–‹å§‹');
    setIsRecording(false); // ã¾ãšéŒ²éŸ³çŠ¶æ…‹ã‚’åœæ­¢ã«è¨­å®š

    if (mediaRecorder) {
      mediaRecorder.stop();
      mediaRecorder.stream.getTracks().forEach(track => track.stop());
      setMediaRecorder(null);
      announce('éŒ²éŸ³ã‚’åœæ­¢ã—ã¾ã—ãŸã€‚', 'assertive');
    }

    console.log('éŒ²éŸ³åœæ­¢å®Œäº†');
  };

  // Blobã‚’ Base64 ã«å¤‰æ›ã™ã‚‹é–¢æ•°
  const blobToBase64 = (blob) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = () => resolve(reader.result);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });
  };

  const saveRecording = async (name, tags) => {
    if (currentRecording && name.trim()) {
      try {
        // Blobã‚’base64ã«å¤‰æ›
        const base64Data = await blobToBase64(currentRecording.audioBlob);

        const savedRecording = {
          ...currentRecording,
          name: name.trim(),
          tags: tags.filter(tag => tag.trim()).map(tag => tag.trim()),
          audioData: base64Data, // base64ãƒ‡ãƒ¼ã‚¿ã‚’ä¿å­˜
          // audioBlobã¯ä¸€æ™‚çš„ãªã‚‚ã®ãªã®ã§å‰Šé™¤
          audioBlob: undefined
        };

        setRecordings([...recordings, savedRecording]);
        setCurrentRecording(null);

        // LocalStorageã«ä¿å­˜ï¼ˆaudioBlobã¯é™¤å¤–ï¼‰
        const existingRecordings = JSON.parse(localStorage.getItem('soundRecordings') || '[]');
        const recordingToSave = { ...savedRecording };
        delete recordingToSave.audioBlob; // Blobã¯ä¿å­˜ã—ãªã„
        localStorage.setItem('soundRecordings', JSON.stringify([...existingRecordings, recordingToSave]));
      } catch (error) {
        console.error('éŒ²éŸ³ã®ä¿å­˜ã«å¤±æ•—ã—ã¾ã—ãŸ:', error);
        alert('éŒ²éŸ³ã®ä¿å­˜ã«å¤±æ•—ã—ã¾ã—ãŸã€‚å†åº¦ãŠè©¦ã—ãã ã•ã„ã€‚');
      }
    }
  };

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('audio/')) {
      const url = URL.createObjectURL(file);
      setCurrentRecording({
        id: Date.now(),
        url: url,
        audioBlob: file,
        name: file.name.replace(/\.[^/.]+$/, ''),
        tags: [],
        createdAt: new Date()
      });
    }
  };

  // ãƒžã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ãƒ†ã‚¹ãƒˆæ©Ÿèƒ½ï¼ˆiOSã§ã®å•é¡Œå¯¾ç­–ï¼‰
  const testMicrophoneAccess = async () => {
    try {
      console.log('ãƒžã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ãƒ†ã‚¹ãƒˆé–‹å§‹');

      // HTTPSæŽ¥ç¶šãƒã‚§ãƒƒã‚¯
      if (!checkHTTPS()) {
        alert('ðŸ”’ éŒ²éŸ³æ©Ÿèƒ½ã‚’ä½¿ç”¨ã™ã‚‹ã«ã¯HTTPSæŽ¥ç¶šãŒå¿…è¦ã§ã™ã€‚\n\niPhoneã§ã¯ç‰¹ã«ã€ã‚»ã‚­ãƒ¥ã‚¢ãªæŽ¥ç¶šãŒå¿…è¦ã¨ãªã‚Šã¾ã™ã€‚');
        return false;
      }

      if (!navigator.mediaDevices || !navigator.mediaDevices.getUserMedia) {
        throw new Error('ãŠä½¿ã„ã®ãƒ–ãƒ©ã‚¦ã‚¶ã¯éŒ²éŸ³æ©Ÿèƒ½ã‚’ã‚µãƒãƒ¼ãƒˆã—ã¦ã„ã¾ã›ã‚“ã€‚');
      }

      // ã¾ãšãƒžã‚¤ã‚¯ã®æ¨©é™çŠ¶æ…‹ã‚’ç¢ºèª
      if (navigator.permissions) {
        try {
          const permission = await navigator.permissions.query({ name: 'microphone' });
          console.log('ãƒžã‚¤ã‚¯æ¨©é™çŠ¶æ…‹:', permission.state);

          if (permission.state === 'denied') {
            alert('ãƒžã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ãŒæ‹’å¦ã•ã‚Œã¦ã„ã¾ã™ã€‚ãƒ–ãƒ©ã‚¦ã‚¶ã®è¨­å®šã‹ã‚‰ãƒžã‚¤ã‚¯ã®ä½¿ç”¨ã‚’è¨±å¯ã—ã¦ãã ã•ã„ã€‚');
            return false;
          }
        } catch (permError) {
          console.log('æ¨©é™ç¢ºèªã¯ã‚µãƒãƒ¼ãƒˆã•ã‚Œã¦ã„ã¾ã›ã‚“:', permError);
        }
      }

      // å®Ÿéš›ã«ãƒžã‚¤ã‚¯ã‚¢ã‚¯ã‚»ã‚¹ã‚’ãƒ†ã‚¹ãƒˆ
      const stream = await navigator.mediaDevices.getUserMedia({ 
        audio: {
          echoCancellation: true,
          noiseSuppression: true,
          autoGainControl: true
        }
      });

      // ã™ãã«ã‚¹ãƒˆãƒªãƒ¼ãƒ ã‚’åœæ­¢
      stream.getTracks().forEach(track => track.stop());
