          if (clipEndTimeInSeconds > currentTimeInSeconds) {
            const delay = Math.max(0, clipStartTimeInSeconds - currentTimeInSeconds);
            if (isFinite(delay) && delay >= 0) {
              scheduleClipPlayback(clip, delay * 1000, newPlayingAudios);
            }
          }
        });
