            URL.revokeObjectURL(audioUrl); // ãƒ¡ãƒ¢ãƒªãƒªãƒ¼ã‚¯ã‚’é˜²ã
          });
        }, delayMs);

        // éŸ³å£°çµ‚äº†æ™‚ã«URLã‚’è§£æ”¾
        audio.addEventListener('ended', () => {
