          // ã‚¯ãƒªãƒƒãƒ—ã®soundDataã‚’æ›´æ–°
          clip.soundData.audioBlob = blob;

          // å†å¸°çš„ã«å†è©¦è¡Œ
          scheduleClipPlayback(clip, delayMs, playingAudiosMap);
          return;
        } catch (restoreError) {
          console.error('audioDataã‹ã‚‰ã®Blobå¾©å…ƒã«å¤±æ•—:', restoreError);
        }
      }
    }
  };

